/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06010130-service:searchExpendableChg" ) {
	    if(dsOutExpendableChg.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutExpendableChg.GetRowCount());
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

