function fcInit(obj) {	
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
	dsOutMaintenance.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(calIjrySdt, "시작일자")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e06010150-service:searchMaintenance";

    if(chkTarget.Value){
		sServiceName = "e06010150-service:searchMaintenanceAll";
    }else{
		sServiceName = "e06010150-service:searchMaintenance";
    }

    var sInDataSet   = "";
    var sOutDataSet  = "dsOutMaintenance=dsOutMaintenance";       
		sOutDataSet += " dsOutMotPropeller=dsOutMotPropeller";

    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("SEARCH_SDT", calIjrySdt.Value);
    fce_AddParam("SEARCH_EDT", calIjryEdt.Value);	

    fce_AddParam("MOT_NO", edMotNo.Value);	
    fce_AddParam("PROPELLER_NO", edProNo.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutMaintenance )  )
	{//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutMaintenance;
	sMandatoryColID   = "MOT_NO|PROPELLER_NO|MAINTENANCE_DT";
	sMandatoryColName = "모터번호|프로펠러번호|점검일자";
	oGrid             = grdMaintenance;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;   

    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("SEARCH_SDT", calIjrySdt.Value);
    fce_AddParam("SEARCH_EDT", calIjryEdt.Value);	

    fce_AddParam("MOT_NO", edMotNo.Value);	
    fce_AddParam("PROPELLER_NO", edProNo.Value);

    fce_AddParam("CHK_TARGET", chkTarget.Value);

    var sServiceName = "e06010150-service:saveMaintenance";
    var sInDataSet   = "dsOutMaintenance=dsOutMaintenance:u";
    var sOutDataSet  = "dsOutMaintenance=dsOutMaintenance";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//cboMbrCd.Value = CURR_USER_MBR_CD;
	if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
    }
	dsOutMaintenance.InsertRow(0);  	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(dsOutMaintenance.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003);		
	}

	dsOutMaintenance.DeleteRow(dsOutMaintenance.currow);

	setProNumber();
}


