//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;		
	fcSearch();

}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//정기점검대상조회 클릭.
var oldEdt = "";
var oldMotNo = "";
var oldProNo = "";

function chkTarget_OnClick(obj,strValue)
{
	if(chkTarget.Value == 1){
		oldEdt = calIjryEdt.Value;
		oldMotNo = edMotNo.Text;
		oldProNo = edProNo.Text;

		calIjryEdt.Value = "";
		edMotNo.Text = "";
		edProNo.Text = "";

		calIjryEdt.Enable = false;
		edMotNo.Enable = false;
		edProNo.Enable = false;
	}else{
		calIjryEdt.Value = oldEdt;
		edMotNm.Text = oldMotNo;
		edProNo.Text = oldProNo;

		calIjryEdt.Enable = true;
		edMotNm.Enable = true;
		edProNo.Enable = true;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  모터조회 공통팝업.
+-------------------------------------------------------------------------------------------------*/
function grdMaintenance_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if((nCell == 0) && dsOutMaintenance.GetRowType(nRow) == "insert"){
		var sArg = "Arg_sEquipTpeCd=M" ;
		sArg += " Arg_sMbrCd=" + cboMbrCd.Value;
		var sResponse = Dialog("form::E06010141.xml", sArg, 584, 432, "");

		if ( !fnc_IsNull(sResponse[0]) ) {
			dsOutMaintenance.SetColumn(nRow, "MOT_NO", sResponse[0]);
			dsOutMaintenance.SetColumn(nRow, "EQUIP_REG_NO", sResponse[1]);
		} else {
		}

	}

	if(nCell == 2){
		dsOutMotPropeller.Filter("MOT_NO='" + dsOutMaintenance.GetColumn(nRow, "MOT_NO") + "'");

		if(fnc_IsNull(dsOutMaintenance.GetColumn(nRow, "MOT_NO"))){
			alert("모터번호를 먼저 선택 하세요!");
			return false;
		}
	}

	if(nCell == 2 && dsOutMaintenance.GetRowType(nRow) != "insert"){
		grdMaintenance.SetCellProp("body",2,"Edit","none"); 
		return false;
	}else{
		grdMaintenance.SetCellProp("body",2,"Edit","combo"); 
	}
}

/*-------------------------------------------------------------------------------------------------+
>> 모터 선택시 보유 프로펠러 목록 조회.
+-------------------------------------------------------------------------------------------------*/
function dsOutMaintenance_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PROPELLER_NO"){
		var seq = dsOutMotPropeller.GetColumn(dsOutMotPropeller.FindRow("PROPELLER_NO", varNewValue), "PROPELLER_SEQ");
		//alert(seq);	
		dsOutMaintenance.SetColumn(nRow, "PROPELLER_SEQ", seq);

	}
}

/*-------------------------------------------------------------------------------------------------+
>> 모터별 순번
+-------------------------------------------------------------------------------------------------*/
function setProNumber(){
	var num = 1;

	grdMaintenance.Redraw = false;

	for(var i=0; i<dsOutMaintenance.GetRowCount(); i++){

		if(i != 0){
			if(dsOutMaintenance.GetColumn(i-1, "MOT_NO") == dsOutMaintenance.GetColumn(i, "MOT_NO")){
				dsOutMaintenance.SetColumn(i, "NUM", num);
				num++;
			}else{
				num = 1;
				dsOutMaintenance.SetColumn(i, "NUM", num);
				num++;
			}
		}else{
			dsOutMaintenance.SetColumn(i, "NUM", num);
			num++;
		}
	}

	grdMaintenance.Redraw = true;
}
