function fcInit(obj) {	
	cboUseYn.value = ''; // 사용중 
	if(sInpt == "F"){
		this.divBtnLst.btnSave.Visible = false;
	}
    dsOutParts.ClearData();

}

//그리드 업체 조회 
function fcGrdSearch(){

	var sServiceName = "e06020020-service:searchPartsCom";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPartsCom=dsOutPartsCom";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);    

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

}

//조회 
function fcSearch(obj) {
	fcSearchCheck();
    //부품 조회 
    fcSearchParts();

}

//부품  조회 
function fcSearchParts(){
	fce_AddParam("PRICE_STND_YEAR", cboStndYear.Value);
	fce_AddParam("PARTS_CD", edPartsCd.Text);
    fce_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fce_AddParam("MODULE_CODE", edModuleCd.Text);
    fce_AddParam("USE_YN", cboUseYn.Value);
    var sServiceName = "e06020011-service:searchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutParts )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
    fce_AddParam("PRICE_STND_YEAR", cboStndYear.Value);
    fce_AddParam("PARTS_CD", edPartsCd.Text);
    fce_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fce_AddParam("MODULE_CODE", edModuleCd.Text);
    fce_AddParam("USE_YN", cboUseYn.Value);
    var sServiceName = "e06020011-service:saveParts";
    var sInDataSet   = "dsOutParts=dsOutParts:u";
    var sOutDataSet  = "dsOutParts=dsOutParts";

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

