/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboSYear, "출납시작년")){
		return false;
   }else if(!fnc_IsMandatory(cboSMonth, "출납시작월")){
		return false;
   }else if(!fnc_IsMandatory(cboEMonth, "출납종료월")){
		return false;
   }else if(parseFloat(cboSMonth.Value) >parseFloat(cboEMonth.Value)) {
		alert("시작월이 종료일보다 큽니다.");
		return false;
   }
   return true;
}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}  
    //부품출납월보 조회 
    fcSearchParts();

}

//부품출납월보  조회 
function fcSearchParts(){
	//fce_AddParam("STND_YEAR", CURR_STNT_YEAR);
	fce_AddParam("ENT_SDT", cboSYear.Value+cboSMonth.Value);    
	fce_AddParam("ENT_EDT", cboSYear.Value+cboEMonth.Value);    
    fce_AddParam('MBR_CD', cboMbrCd.Value);
    fce_AddParam('STND_YEAR', cboSYear.Value);
    fce_AddParam('MODULE_CODE', edModuleCd.Text);
    fce_AddParam('PARTS_CD', edPartsCd.Text);
    fce_AddParam('PARTS_NM', edItemCdNm.Text);

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";

    if (edAllChk.Value == '1') sServiceName = "e06030021-service:searchOutPartsMonthAll";
    else                       sServiceName = "e06030021-service:searchOutPartsMonth";

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}
