/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06039021_outparts_monthr.jrf";               //레포트 파일명
	var sDataSet  = "dsOutParts=dsOutParts";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboSYear.Value;
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}


//Grit Format생성 
function setMonthHeader(a,b){
	var sMonth = parseFloat(a);
    var eMonth = parseFloat(b);

  	var gridFormat = '';
	gridFormat += " <contents> ";
	gridFormat += " 	<format id='Default'> ";

	gridFormat += " 		<columns> ";
	gridFormat += " 			<col fix='left' width='90'/> ";	
	gridFormat += " 			<col fix='left' width='90'/> ";
	gridFormat += " 			<col fix='left' width='150'/> ";
	gridFormat += " 			<col fix='left' width='0'/> ";	
	gridFormat += " 			<col fix='left' width='60'/> ";

	for(var j=1; j<=(eMonth-sMonth)+1; j++){
		gridFormat += " 		<col width='45'/> ";
		gridFormat += " 		<col width='45'/> ";
	}
		gridFormat += " 		<col width='45'/> ";
		gridFormat += " 		<col width='45'/> ";
		gridFormat += " 		<col width='60'/> ";
	gridFormat += " 		</columns> ";

	gridFormat += " 		<head> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' rowspan='2' text='식별번호'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' rowspan='2' text='품번'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' rowspan='2' text='품명'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='3' color='user21' display='text' font='굴림,9' rowspan='2' text='규격'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' col='4' color='user21' display='text' font='굴림,9' rowspan='2' text='전년(월)&#32;&#32;이월' wordwrap='word'/> ";
	var columnIndex = 5;		
	for(var i=sMonth; i<=(eMonth-sMonth)+sMonth; i++){
		gridFormat += " 			<cell align='center' bkcolor='user20' col='"+(columnIndex++)+"' color='user21' colspan='2' display='text' font='굴림,9' text='"+(i)+"월'/> ";		
		gridFormat += " 			<cell align='center' bkcolor='darksalmon' col='"+(columnIndex-1)+"' color='user21' display='text' font='굴림,9' row='1' text='입고'/> ";
		gridFormat += " 			<cell align='center' bkcolor='khaki' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' row='1' text='출고'/> ";				
	}

	gridFormat += " <cell align='center' bkcolor='user20' col='"+(columnIndex++)+"' color='user21' colspan='2' display='text' font='굴림,9' text='합계'/> ";
	gridFormat += " <cell align='center' bkcolor='darksalmon' col='"+(columnIndex-1)+"' color='user21' display='text' font='굴림,9' row='1' text='입고'/> ";
	gridFormat += " <cell align='center' bkcolor='khaki' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' row='1' text='출고'/> ";
	gridFormat += " <cell align='center' bkcolor='user20' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' rowspan='2' text='차년이월&#32;(재고)' wordwrap='word'/> ";
	gridFormat += " 		</head> ";

	gridFormat += " 		<body> ";
	gridFormat += " 			<cell align='center' col='0' colid='MODULE_CODE' display='text' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
	gridFormat += " 			<cell align='center' col='1' colid='PARTS_CD' display='text' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
	gridFormat += " 			<cell align='left' col='2' colid='PARTS_ITEM_CD_NM' display='text' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
	gridFormat += " 			<cell col='3' colid='SPEC' display='text' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
	gridFormat += " 			<cell align='right' col='4' colid='CRFW_UNIT_CNT' display='number' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";

	var bodyColumnIndex =5;
	for(var i=sMonth; i<=(eMonth-sMonth)+sMonth; i++){
		gridFormat += " 		<cell align='right' col='"+(bodyColumnIndex++)+"' colid='MONTH_"+i+"_ENT' display='number' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
		gridFormat += " 		<cell align='right' col='"+(bodyColumnIndex++)+"' colid='MONTH_"+i+"_DELV' display='number' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
	}		
	gridFormat += " 			<cell align='right' col='"+(bodyColumnIndex++)+"' colid='MONTH_TOTAL_ENT' display='number' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
