	gridFormat += " 			<cell align='right' col='"+(bodyColumnIndex++)+"' colid='NOW_UNIT_CNT' display='number' bkcolor='expr:decode(dsOutParts.GetColumn(row,&apos;MODULE_CODE&apos;),&apos;보트계&apos;,&apos;lightblue&apos;,&apos;모터계&apos;,&apos;bisque&apos;,&#32;&apos;합계&apos;,&apos;yellowgreen&apos;,&apos;default&apos;)'/> ";
