/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }
   return true;
}

//조회 
function fcSearch(obj) {
	fcSearchCheck();
    //부품출납총괄표 조회 
    fcSearchParts();

}

//부품출납총괄표  조회 
function fcSearchParts(){
	//fce_AddParam("STND_YEAR", CURR_STNT_YEAR);
	fce_AddParam('MBR_CD', cboMbrCd.Value);
    fce_AddParam('STND_YEAR', cboStndYear.Value);
    fce_AddParam('MM', cboMonth.Value);

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";	

    if (Trim(cboMonth.Value) == "" || fnc_IsNull(cboMonth.Value))
		sServiceName = "e06030040-service:searchOutPartsTotal";
    else
		sServiceName = "e06030040-service:searchOutPartsMonthTotal";

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);    

}
