/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {

    }else if( sServiceName == "e06030040-service:searchOutPartsTotal" ) {
	    if(dsOutParts.GetRowCount()>0){			
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());

			grdOutParts.SetCellProp("Head", 1, "expr", "'이월('+("+cboStndYear.Value+"-1)+'년)'" );
			grdOutParts.SetCellProp("Head", 2, "expr", "'입고('+("+cboStndYear.Value+")+'년)'" );
			grdOutParts.SetCellProp("Head", 3, "expr", "'출고('+("+cboStndYear.Value+")+'년)'" );
			grdOutParts.SetCellProp("Head", 4, "expr", "'재고('+("+cboStndYear.Value+")+'년)'" );
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());
	    }	   	    
    }else if( sServiceName == "e06030040-service:searchOutPartsMonthTotal" ) {
	    if(dsOutParts.GetRowCount()>0){			
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());

			grdOutParts.SetCellProp("Head", 1, "expr", "'이월('+("+cboStndYear.Value+")+'년 '+("+cboMonth.Value+"-1)+'월)'" );
			grdOutParts.SetCellProp("Head", 2, "expr", "'입고('+("+cboMonth.Value   +")+'월)'" );
			grdOutParts.SetCellProp("Head", 3, "expr", "'출고('+("+cboMonth.Value   +")+'월)'" );
			grdOutParts.SetCellProp("Head", 4, "expr", "'재고('+("+cboStndYear.Value+")+'년 '+("+cboMonth.Value+")+'월)'" );
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());
	    }	   	    
	}	
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

