/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

   if(!fnc_IsMandatory(cboMbrCd, "경정장")){
		return false;
   }else if(!fnc_IsMandatory(calStrDt, "주문시작일자")){
		return false;
   }else if(!fnc_IsMandatory(calEndDt, "주문종료일자")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e06050030-service:searchOrder";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList";       

    fce_AddParam("ORDER_STR_DT", calStrDt.Value);
    fce_AddParam("ORDER_END_DT", calEndDt.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//주문세부내역 조회
function fcSearchOrderDetail()
{
    var sServiceName = "e06050030-service:searchOrderDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderSuppList=dsOutOrderSuppList";       

    fce_AddParam("JUMUN_NO", dsOutOrderList.GetColumn(dsOutOrderList.row, "JUMUN_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutOrderList ) )
	{
		if ( !fnc_DatasetChangeCheck(dsOutOrderSuppList )  )
		{	
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}
	}

	// 필수항목체크
	oDataSet          = dsOutOrderSuppList;
	sMandatoryColID   = "SUPP_CD|ORDER_CNT|AMOUNT";
	sMandatoryColName = "소모품코드|주문수량|주문금액";
	oGrid             = grdOrderSuppList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}



function fcSave(obj) {
    if ( !fcSaveCheck() ) return;   

    fce_AddParam("ORDER_STR_DT", calStrDt.Value);
    fce_AddParam("ORDER_END_DT", calEndDt.Value);

    var sServiceName = "e06050030-service:saveOrderSupp";
    var sInDataSet   = "dsOutOrderSuppList=dsOutOrderSuppList:u";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList"; 

   	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

    if(obj.GetForm().id= "divAddOrder" ) {
		var nRowType = dsOutOrderList.GetRowType(dsOutOrderList.row);
		if (ToUpper(nRowType) == "INSERT" || ToUpper(nRowType) == "UPDATE") {
			if (fnc_message("저장되지 않는 주문서의 변경사항을 저장하시겠습니까?","Y")) {
				fcSave(obj);
				return;
			}			
		}		
		var nRow = dsOutOrderList.AddRow();
		dsOutOrderList.SetColumn(nRow, "JUMUN_NO", today());	
    } else if(obj.GetForm().id= "divAddSupp" ) {
		// 소모품리스트 전체가 조회되는 팝업에서 구매할 품목을 선택하는 팝업 호출
		var args  = "";
		var bRtn = Dialog("form::E06050011P.xml", args, 544, 584, "");

		if(bRtn) {	
			grdOrderSuppList.Redraw = false;
			var nRow = 0;			
			for (var i=0;i<gdsReturn.rowcount();i++) {
				//동일한 것이 있는 지 여부 체크 : 있으면 추가하지 않음(부품코드,단가년도)
				var sSuppCd = gdsReturn.GetColumn(i, "SUPP_CD");
				if (dsOutOrderSuppList.SearchRow("SUPP_CD='" + sSuppCd + "'") <> -1) continue;

				var nRow = dsOutOrderSuppList.AddRow();
				dsOutOrderSuppList.SetColumn(nRow, "IO_DT", 		dsOutOrderList.GetColumn(dsOutOrderList.row, "JUMUN_NO"));
				dsOutOrderSuppList.SetColumn(nRow, "JUMUN_NO", 		dsOutOrderList.GetColumn(dsOutOrderList.row, "JUMUN_NO"));
				dsOutOrderSuppList.SetColumn(nRow, "IO_TYPE", 		"2");
				dsOutOrderSuppList.SetColumn(nRow, "SUPP_CD", 		sSuppCd);
				dsOutOrderSuppList.SetColumn(nRow, "SUPP_NM", 		gdsReturn.GetColumn(i, "SUPP_NM"));
				dsOutOrderSuppList.SetColumn(nRow, "SPEC", 			gdsReturn.GetColumn(i, "SPEC"));
				dsOutOrderSuppList.SetColumn(nRow, "DANGA", 		gdsReturn.GetColumn(i, "DANGA"));
			}
			grdOrderSuppList.Redraw = true;
		}		
    }

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    if(obj.GetForm().id= "divAddOrder" ) {
		if (dsOutOrderSuppList.rowcount() > 0) {
			if (fnc_message(dsOutOrderList.GetColumn(dsOutOrderList.row, "JUMUN_NO")+"자 주문내역을 삭제하시겠습니까?", "Y") == true) {
				for (var iCnt=(dsOutOrderSuppList.rowcount() -1);iCnt >= 0;iCnt--) {
					dsOutOrderSuppList.DeleteRow(iCnt);
				}
				fcSave(obj);
			}
			return;
		} else {
			dsOutOrderList.DeleteRow(dsOutOrderList.row);
		}
		//dsOutOrderList.DeleteRow(dsOutOrderList.row);
    } else if(obj.GetForm().id= "divAddSupp" ) {
		dsOutOrderSuppList.DeleteRow(dsOutOrderSuppList.row);
    }
}


