/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06050030-service:searchOrder" ) {
		if(dsOutOrderList.GetRowCount()>0){
			if(edsIndex.Text ==null || edsIndex.Text ==''){
				edsIndex.Text = 0;
				//fcSearchOrderDetail(0);				
			}else{
				//fcSearchOrderDetail(edsIndex.Text);
			}			
		}else{
			dsOutOrderSuppList.ClearData();
		}


	    if(dsOutOrderSuppList.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutOrderSuppList.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }

        edpartsIndex.Text = 0;
	}else if(sServiceName == "e06050030-service:searchOrderParts"){
		if(dsOutOrderSuppList.GetRowCount()>0){
			//fcSetMasterCon();
		}		
	}
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


