function btnPlus_OnClick(obj)
{
	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "ENT_STATE") !='001'){
		fnc_Message(SNIS_BOA_F003, "N");	
		return;
	}

	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "PARTS_COM_CD") = null){
		fnc_Message(SNIS_BOA_F012, "N");	
		return;
	}

	if(dsOutOrderList.GetRowCount()>0){

		if(edpartsIndex.Text ==''){
			alert("선택된 부품이 없습니다. ");
			return false;
		}	
		if(dsOutOrderList.GetRowCount()==1){
			dsOutOrderSuppList.AddRow();  
			var mbrcd =dsOutOrderList.getColumn(0, "MBR_CD");
			var year =dsOutOrderList.getColumn(0, "STND_YEAR");
			var order_seq =dsOutOrderList.getColumn(0, "ORDER_SEQ");

			var prats_cd = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_CD");
			var prats_seq = dsOutparts.getColumn(edpartsIndex.Text, "PRICE_STND_YEAR");
			var prats_item_cd_nm = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_ITEM_CD_NM");
			var spec = dsOutparts.getColumn(edpartsIndex.Text, "SPEC");

			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"STND_YEAR",year);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"MBR_CD",mbrcd);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"ORDER_SEQ",order_seq);

			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PARTS_CD",prats_cd);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PRICE_STND_YEAR",prats_seq);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PARTS_ITEM_CD_NM",prats_item_cd_nm);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"SPEC",spec);

			dsOutOrderSuppList.SetColumn(edpartsIndex.Text,"ORDER_SEQ",order_seq);

			//품번이 같은게 있음 체크한다.
			var count = 0;
			var parts_code = dsOutOrderSuppList.GetColumn(dsOutOrderSuppList.Row, "PARTS_CD");			
			for(var i = 0 ; i <= dsOutOrderSuppList.GetRowCount(); i++) {
				if(parts_code == dsOutOrderSuppList.GetColumn(i, "PARTS_CD")){
					//alert("2:"+i);
					count = count + 1;
				}
			}

			if(count > 1){
				fnc_SetStatus(L_COM_ALT_0008,  dsOutOrderSuppList.GetRowCount());		
				dsOutOrderSuppList.DeleteRow(dsOutOrderSuppList.Row);
				return false;	
			}		



		}else{
			if(edsIndex.Text ==''){
				alert("선택된 주문서가 없습니다.");
				return false;
			}
			dsOutOrderSuppList.AddRow();  
			var mbrcd =dsOutOrderList.getColumn(edsIndex.Text, "MBR_CD");
			var year =dsOutOrderList.getColumn(edsIndex.Text, "STND_YEAR");
			var order_seq =dsOutOrderList.getColumn(edsIndex.Text, "ORDER_SEQ");

			var prats_cd = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_CD");
			var prats_seq = dsOutparts.getColumn(edpartsIndex.Text, "PRICE_STND_YEAR");
			var prats_item_cd_nm = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_ITEM_CD_NM");
			var spec = dsOutparts.getColumn(edpartsIndex.Text, "SPEC");
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"STND_YEAR",year);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"MBR_CD",mbrcd);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"ORDER_SEQ",order_seq);

			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PARTS_CD",prats_cd);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PRICE_STND_YEAR",prats_seq);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"PARTS_ITEM_CD_NM",prats_item_cd_nm);
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"SPEC",spec);

			//품번이 같은게 있음 체크한다.
			var count = 0;
			var parts_code = dsOutOrderSuppList.GetColumn(dsOutOrderSuppList.Row, "PARTS_CD");

			for(var i = 0 ; i <= dsOutOrderSuppList.GetRowCount(); i++) {
				if(parts_code == dsOutOrderSuppList.GetColumn(i, "PARTS_CD")){
					//alert("2:"+i);
					count = count + 1;
				}
			}

			if(count > 1){
				fnc_SetStatus(L_COM_ALT_0008,  dsOutOrderSuppList.GetRowCount());		
				dsOutOrderSuppList.DeleteRow(dsOutOrderSuppList.Row);
				return false;	
			}		
		}

		for(var i = 1 ; i <= dsOutOrderSuppList.GetRowCount(); i++) {
			dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"NUM",i);		
		}
	}
}

