function btnMinus_OnClick(obj)
{
	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "ENT_STATE") !='001'){
		fnc_Message(SNIS_BOA_F003, "N");	
		return;
	}

	if(dsOutOrderSuppList.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003,  dsOutOrderSuppList.GetRowCount());		
	}

	dsOutOrderSuppList.DeleteRow(dsOutOrderSuppList.Row);

	for(var i = 1 ; i <= dsOutOrderSuppList.GetRowCount(); i++) {
		dsOutOrderSuppList.SetColumn(dsOutOrderSuppList.rowposition,"NUM",i);
	}	
}

function dsOutOrderList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	if(strColumnID =="ORDER_DT"){	
		calIjryEdt.Value = dsOutOrderList.GetColumn(nRow, "ORDER_DT");			
	}else if(strColumnID =="PARTS_COM_CD"){
		//부품리스트 조회버튼 클릭 시

		fce_AddParam("STND_YEAR", cboStndYear.Value);	
		fce_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
		fce_AddParam('MBR_CD', cboMbrCd.Value);    
		fce_AddParam('PARTS_COM_CD', dsOutOrderList.GetColumn(nRow, "PARTS_COM_CD"));    
		var sServiceName = "e06020010-service:searchPartsByOrder";
		var sInDataSet   = "";
		var sOutDataSet  = "dsOutParts=dsOutParts";
		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	}
}

