function dsOutOrderSuppList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "ORDER_CNT" and varOldValue != varNewValue){
		dsOutOrderSuppList.SetColumn(nRow, "AMOUNT", toNumber(dsOutOrderSuppList.GetColumn(nRow, "ORDER_CNT")) * toNumber(dsOutOrderSuppList.GetColumn(nRow, "DANGA")));
		genOrderCon();
	}
}


function genOrderCon(){
	var rowCnt = dsOutOrderSuppList.GetRowCount();
	var orderPartsCnt = 0;
	var orderCon = "";
	var firstPartsNm = "";

	for(i=0; i<rowCnt; i++){
		orderPartsCnt += dsOutOrderSuppList.GetColumn(i, 'ORDER_CNT');
		if(i==0)firstPartsNm = dsOutOrderSuppList.GetColumn(i, 'SUPP_NM');		
	}

	if(rowCnt>1){
		orderCon = firstPartsNm + ' ' + '외 ' + (rowCnt-1) +'개 ';
	}else{
		orderCon = firstPartsNm ;
	}
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition, 'ORDER_CON', orderCon);
}

function dsOutSuppList_OnRowDeleted(obj,nRow,nCount)
{
	genOrderCon();
}

function dsOutOrderSuppList_OnRowInserted(obj,nRow,nCount)
{
	genOrderCon();
}

function dsOutOrderList_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchOrderDetail();
}

function dsOutOrderList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcSearchOrderDetail();
}

function getSum()
{
	var nSum = 0;
	for (var i=0;i<dsOutOrderSuppList.rowcount();i++) {
		nSum += toNumber(dsOutOrderSuppList.GetColumn(i, "DANGA")) * toNumber(dsOutOrderSuppList.GetColumn(i, "ORDER_CNT"));
	}
	return nSum;
}
function dsOutOrderSuppList_OnRowDeleted(obj,nRow,nCount)
{
	genOrderCon();
}

function fcgrdOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	switch (obj.id) {
