}

function fcSave(obj) {

    var sServiceName = "e07020055-service:saveMonthPay";
    var sInDataSet   = "dsOutMonthPay=dsOutMonthPay:u dsOutMonthPayStat=dsOutMonthPayStat";
    var sOutDataSet  = "dsOutMonthPay=dsOutMonthPay dsOutMonthPayStat=dsOutMonthPayStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("PAY_MONTH", cboTms.Value);
    fce_AddParam("RACER_NM", edRacerNm.Text);	
    fce_AddParam("PAY_DT",    calPayDay.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

