+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	// 출력용 DataSet 만들기
	buildSumDataSet();

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e07029055_month_pay.jrf";               //레포트 파일명
	    sRptNm    = "e07029055_month_pay_2.jrf";               //레포트 파일명
	var sDataSet  = "dsOutMonthPay=dsOutMonthPay_rpt";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg += " STND_MONTH="+cboPayMonth.Value; 
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	//dsOutMonthPay.DeleteRow(dsOutMonthPay.rowposition);
}



function buildSumDataSet(){

	dsOutMonthPay_rpt.ClearData();
	// 조회 데이터를 출력용 DataSet에 복사
	dsOutMonthPay_rpt.Copy("dsOutMonthPay");

	// 합계 계산
	var stndYear = grdMonthPay.GetCellText("Summary",-1,0);	 	//기준년도
	var payMonth = grdMonthPay.GetCellText("Summary",-1,1);	 	//기준 월 
	var raceNo = Replace(grdMonthPay.GetCellText("Summary",-1,2), ',', '');	 		//선수번호 필드 
	var raceNm = Replace(grdMonthPay.GetCellText("Summary",-1,3), ',', '');  		//선수명 필드
	var suppAmt01 = Replace(grdMonthPay.GetCellText("Summary",-1,4), ',', '');		//성적상금 필드 
	var suppAmt02 = Replace(grdMonthPay.GetCellText("Summary",-1,5), ',', '');		//출전수당 필드 
	var suppAmt03 = Replace(grdMonthPay.GetCellText("Summary",-1,6), ',', '');		//이벤트수당 필드 
	var suppAmt04 = Replace(grdMonthPay.GetCellText("Summary",-1,7), ',', '');		//안전수당 필드 
	var suppAmt05 = Replace(grdMonthPay.GetCellText("Summary",-1,8), ',', '');		//대기수당 필드 
	var suppAmt06 = Replace(grdMonthPay.GetCellText("Summary",-1,9), ',', '');		//출발무사고 필드
	var suppAmt07 = Replace(grdMonthPay.GetCellText("Summary",-1,10), ',', '');		//연승상금 필드 
	var suppAmt08 = Replace(grdMonthPay.GetCellText("Summary",-1,11), ',', '');		//기타수당 필드
	var suppAmt10 = Replace(grdMonthPay.GetCellText("Summary",-1,12), ',', '');		//식대 필드 
	var suppAmt12 = Replace(grdMonthPay.GetCellText("Summary",-1,13), ',', '');		//선수연금(사업자부담) 필드 
	var aidAmt05 = Replace(grdMonthPay.GetCellText("Summary",-1,14), ',', '');		//선수연금(개인부담) 필드 
	var aidAmt04 = Replace(grdMonthPay.GetCellText("Summary",-1,15), ',', '');		//선수회비 필드 

	// 합계 Row 추가.
	dsOutMonthPay_rpt.AddRow();
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'STND_YEAR', stndYear);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'PAY_MONTH', payMonth);
	//dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'RACER_NO', raceNo);
	//dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'RACER_NM', raceNm);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_01', suppAmt01);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_02', suppAmt02);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_31', suppAmt03);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_03', suppAmt04);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_04', suppAmt05);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_05', suppAmt06);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_06', suppAmt07);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_07', suppAmt08);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_10', suppAmt10);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'SUPP_AMT_12', suppAmt12);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'AID_AMT_05', aidAmt05);
	dsOutMonthPay_rpt.SetColumn(dsOutMonthPay_rpt.rowposition, 'AID_AMT_04', aidAmt04);
}
