/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e07020055-service:searchTmsPay" ) {
    	fnc_SetStatus(SNIS_BOA_0003,  dsOutMonthPay.GetRowCount());

	} else if (sServiceName == "e07020055-service:calcMonthPay" || sServiceName == "e07020055-service:searchMonthPay") {
		calPayDay.value = dsOutMonthPayStat.GetColumn(0, "PMT_DT");
	}


	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

