function dsOutMonthPayStat_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var monthPrior = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIOR');
	var monthPrizFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN');
	var monthActFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ACT_FINISH_YN');

	var desc = monthPrior;
	if(monthPrizFinish=='X'){
		desc += '   ,월 상금 계산 전';
	}else if(monthPrizFinish == 'N'){
		desc += '  ,월 상금 계산 처리중';
	}else if(monthPrizFinish == 'Y'){
		desc += '  ,월 상금 계산 마감완료';
	}

	if(monthActFinish=='X'){
		desc += '   ,회계 계산 전';
	}else if(monthActFinish == 'N'){
		desc += '  ,회계 계산 처리중';
	}else if(monthActFinish == 'Y'){
		desc += '  ,회계 계산(상금추가) 마감완료';
	}

	if(monthActFinish=="Y"){
		calPayDay.Enable = false;
		btnCalc.Visible = false;
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = false;
		grdMonthPay.Editable = false;
	}else if(monthPrizFinish=="Y"){	
		calPayDay.Enable = false;
		btnCalc.Visible = false;
		btnConfirm.Visible = false;
		if(monthActFinish !='Y')btnConfirmCancel.Visible = true;
		this.divBtnLst.btnSave.Visible = false;
		grdMonthPay.Editable = false;
	}else{
		calPayDay.Enable = true;
		btnCalc.Visible = true;
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthPay.Editable = true;

	}
	labMonthPayStatDesc.Text = desc;
}




function fcCalcMonthPay() {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e07020055-service:calcMonthPay";
    var sInDataSet   = "dsOutMonthPayStat=dsOutMonthPayStat";
    var sOutDataSet  = "dsOutMonthPay=dsOutMonthPay dsOutMonthPayStat=dsOutMonthPayStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("PAY_MONTH", cboTms.Value);
    fce_AddParam("RACER_NM",  edRacerNm.Text);
    fce_AddParam("PAY_DT",    calPayDay.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function btnConfirm_OnClick(obj)
{
	dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN', 'Y');
	fcSave();
}

function btnConfirmCancel_OnClick(obj)
{
	dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN', 'N');
	fcSave();
}


function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value < "2014") {
		stTms.Value = "월";
		dsOutTms.copy(dsOutPayMonth); 
	} else {
		stTms.Value = "회차"; 
		fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");	
	}
}

function cboPayMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

//월 상금 계산 
function btnCalc_OnClick(obj)
{
	if (fnc_isNull(calPayDay.Value)) {
		var sPmtDtEx = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, "PMT_DT_EX");
		if (fnc_message("상금지급일을 입력해야 합니다. \n"+sPmtDtEx+"일로 설정하시겠습니까?","Y")) {
			calPayDay.Value = sPmtDtEx;
		}
		calPayDay.SetFocus();
		return ;
	} 

	var monthPrizFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_PRIZ_FINISH_YN');
	if ( monthPrizFinish =='X' && fnc_Message(SNIS_BOA_G010, "Y" , cboStndYear.Value, cboTms.Value) ) {
		fcCalcMonthPay();	
	}else if ( monthPrizFinish =='N' && fnc_Message(SNIS_BOA_G011, "Y" , cboStndYear.Value, cboTms.Value) ) {
		fcCalcMonthPay();	
	}
}

function dsOutMonthPay_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'SUPP_AMT_10'){
		dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'AID_AMT_10', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_12'){
		dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'AID_AMT_12', varNewValue);
	}

	var suppAmt01 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_01'));
	var suppAmt02 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_02'));
	var suppAmt03 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_31'));
	var suppAmt04 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_03'));
	var suppAmt05 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_04'));
	var suppAmt06 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_05'));
	var suppAmt07 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_06'));
	var suppAmt08 = ToInteger(dsOutMonthPay.GetColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_07'));
    var totAmt = suppAmt01 + suppAmt02 + suppAmt03 + suppAmt04 + suppAmt05 + suppAmt06 + suppAmt07 + suppAmt08;
    if(totAmt>0){
		dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'AID_AMT_06', dsOutMonthPay.getColumn(dsOutMonthPay.rowposition, 'SUPP_AMT_12'));
    }else{
		dsOutMonthPay.SetColumn(dsOutMonthPay.rowposition, 'AID_AMT_06', 0);
    }

}

//경정장 DataSet 로드시 
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}
//회차 DataSet Load 완료 시 
function dsOutTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboTms.Value = CURR_TMS;
	fcSearchLastTms();
}
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value == "2014" && toNumber(cboTms.Value) <= 7) {
		stTms.text = "월";
	} else if (cboStndYear.Value == "2014") {
		stTms.text = "회차";
	}
	fcSearch();
}


function fcSearchLastTms()
{	
    var sServiceName = "e07020055-service:searchLastPayTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastTms=dsLastTms";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD",    cboMbrCd.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
function dsLastTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (dsLastTms.rowcount() > 0) {
		cboTms.Value = dsLastTms.GetColumn(0, "TMS");
	}
}
