/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	http.sync= true;
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);


    //년도조회
    fce_SetStndYear(dsOutStndYear, toInteger(CURR_STNT_YEAR));

	fce_GetRaceTms(cboStndYear.Value, CURR_USER_MBR_CD, null, "dsOutPayMonth");	
	cboPayMonth.Index = 0;

	fcSearchTaxRate();
	//공통코드 조회
	//fce_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();

	http.sync = false;

	fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
