		this.divBtnLst.btnSave.Visible = false;
	}
	//cboPayMonth.Value = toInteger(SubStr(CURR_DT,4,2));
	fcSearchLastTms();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
   }else if(!fnc_IsMandatory(cboPayMonth, "기준월")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e07030011-service:searchMonthPayFncEtc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutMonthPayFncEtc=dsOutMonthPayFncEtc dsOutMonthPayStat=dsOutMonthPayStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("PAY_MONTH", cboPayMonth.Value);
    fce_AddParam("RACER_NM", edRacerNm.Text);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


