function fcSave(obj) {
	var monthEtcFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ETC_FINISH_YN');

    var sServiceName = "e07030011-service:saveMonthPayFncEtc";
    var sInDataSet   ="";

    if(monthEtcFinish=='' || monthEtcFinish=='X'){
		sInDataSet = "dsOutMonthPayFncEtc=dsOutMonthPayFncEtc dsOutMonthPayStat=dsOutMonthPayStat";
	}else{
		sInDataSet = "dsOutMonthPayFncEtc=dsOutMonthPayFncEtc:u dsOutMonthPayStat=dsOutMonthPayStat";
	}
    var sOutDataSet  = "dsOutMonthPayFncEtc=dsOutMonthPayFncEtc dsOutMonthPayStat=dsOutMonthPayStat";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("PAY_MONTH", cboPayMonth.Value);
    fce_AddParam("RACER_NM", edRacerNm.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

