function btnConfirm_OnClick(obj)
{
	dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ETC_FINISH_YN', 'Y');
	fcSave();
}

function btnConfirmCancel_OnClick(obj)
{
	dsOutMonthPayStat.SetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ETC_FINISH_YN', 'N');
	fcSave();
}


function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value < "2014") {
		stPayMonth.Value = "월";
	} else {
		stPayMonth.Value = "회차"; 
		fce_GetRaceTms(cboStndYear.Value, CURR_USER_MBR_CD, null, "dsOutPayMonth");	
	}
}

function cboPayMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (cboStndYear.Value == "2014" && toNumber(cboPayMonth.Value) <= 7) {
		stPayMonth.text = "월";
	} else if (cboStndYear.Value == "2014") {
		stPayMonth.text = "회차";
	}
	fcSearch();
}



function dsOutMonthPayStat_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var monthEtcFinish = dsOutMonthPayStat.GetColumn(dsOutMonthPayStat.rowposition, 'MONTH_ETC_FINISH_YN');

	var desc = "";

	if(monthEtcFinish == 'N'){
		desc += '  기타지급 처리중 ';
	}else if(monthEtcFinish == 'Y'){
		desc += '  기타지급 마감완료';
	}

	if(monthEtcFinish=="Y"){
		btnConfirm.Visible = false;
		btnConfirmCancel.Visible = true;
		this.divBtnLst.btnSave.Visible = false;
		grdMonthFncEtcPay.Editable = false;
		tbnExcelUpload.Visible = false;
	}else if(monthEtcFinish=="N"){
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthFncEtcPay.Editable = true;
		tbnExcelUpload.Visible = true;
	}else{
		btnConfirm.Visible = true;
		btnConfirmCancel.Visible = false;
		this.divBtnLst.btnSave.Visible = true;
		grdMonthFncEtcPay.Editable = true;
		tbnExcelUpload.Visible = true;

	}
	labMonthPayStatDesc.Text = desc;
}

function dsOutMonthPayFncEtc_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'SUPP_AMT_10'){ //식대 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_10', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_12'){ //선수연금 사업사 부담 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_12', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_14'){ //상해보험료 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_11', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_15'){ //현물제공  
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_13', varNewValue);
	}else if(strColumnID == 'SUPP_AMT_16'){ //기타항목 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_14', varNewValue);
	}


	if(strColumnID=='SUPP_AMT_17' || strColumnID=='SUPP_AMT_10' || strColumnID=='SUPP_AMT_12' || strColumnID=='SUPP_AMT_14' ||
		strColumnID=='SUPP_AMT_15' || strColumnID=='SUPP_AMT_16' || strColumnID=='SUPP_AMT_19' || strColumnID=='SUPP_AMT_20'
	){
		var suppAmt07 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_07'));
		var suppAmt10 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_10'));
		var suppAmt12 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_12'));
		var suppAmt14 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_14'));
		var suppAmt15 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_15'));
		var suppAmt16 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_16'));
		var suppAmt19 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_19'));
		var suppAmt20 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_20'));

		var suppTotAmt = suppAmt07 + suppAmt10 + suppAmt12 + suppAmt14 + suppAmt15 + suppAmt16 + suppAmt19 + suppAmt20;
		var aidAmt01 = Floor(suppTotAmt*INCE_TAX_RATE/100*0.1)*10; //주민세 
		var aidAmt02 = Floor(aidAmt01*0.1*0.1)*10; //소득세 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_01', aidAmt01);
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_02', aidAmt02);

	}else if(strColumnID=='AID_AMT_01' ){ //주민세 변경시 
		var aidAmt01 = ToInteger(dsOutMonthPayFncEtc.getColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_01'));
		var aidAmt02 = Floor(aidAmt01*INH_TAX_RATE/100*0.1)*10; //소득세 
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_02', aidAmt02);
	}
}


//Excel Upload
function tbnExcelUpload_OnClick(obj)
{
	var strFileName;
	var nRow;
	var TR_AMT_IN;
	var TR_AMT_OUT;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsOutMonthPayFncEtc.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;

	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();

	for (var i = 3; i < nrow; i++)
	{
		if( trim(dsOutSheet.GetColumn(i, 'Col05'))=='계:') continue;
		dsOutMonthPayFncEtc.AddRow();
		dsOutSheet.CopyToRow(i, dsOutMonthPayFncEtc, dsOutMonthPayFncEtc.rowposition, "STND_YEAR=Col01,PAY_MONTH=Col02,PAY_TPE=Col03,NUM=Col04,RACER_NO=Col05,RACER_NM=Col06,SUPP_AMT_19=Col07,SUPP_AMT_20=Col08,SUPP_AMT_07=Col09,SUPP_AMT_10=Col10,SUPP_AMT_12=Col11,SUPP_AMT_14=Col12,SUPP_AMT_15=Col13,SUPP_AMT_16=Col14,AID_AMT_01=Col15,AID_AMT_02=Col16,AID_AMT_03=Col17");

		//소득세 주민세 재계산 
		if(chkTaxApply.value == 1){
			var suppAmt07 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_07'));
			var suppAmt10 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_10'));
			var suppAmt12 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_12'));
			var suppAmt14 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_14'));
			var suppAmt15 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_15'));
			var suppAmt16 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_16'));
			var suppAmt19 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_19'));
			var suppAmt20 = ToInteger(dsOutMonthPayFncEtc.GetColumn(dsOutMonthPayFncEtc.rowposition, 'SUPP_AMT_20'));

			var suppTotAmt = suppAmt07 + suppAmt10 + suppAmt12 + suppAmt14 + suppAmt15 + suppAmt16 + suppAmt19 + suppAmt20;
			var aidAmt01 = Floor(suppTotAmt*INCE_TAX_RATE/100*0.1)*10; //주민세 
			var aidAmt02 = Floor(aidAmt01*INH_TAX_RATE/100*0.1)*10; //소득세 
			dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_01', aidAmt01);
			dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'AID_AMT_02', aidAmt02);
		}

		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'STND_YEAR', cboStndYear.Value);
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'PAY_MONTH', cboPayMonth.Value);
		dsOutMonthPayFncEtc.SetColumn(dsOutMonthPayFncEtc.rowposition, 'PAY_TPE', '6');

	}
}


//원천징수 의무자 정보 세액 정보 추출 
function fcSearchTaxRate() {
	fcSearchCheck();

	var sServiceName = "e07010020-service:searchComInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutComInfo=dsOutComInfo";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



function dsOutComInfo_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	INCE_TAX_RATE = ToInteger(dsOutComInfo.GetColumn(dsOutComInfo.rowposition, 'INCE_TAX_RATE'));
	INH_TAX_RATE = ToInteger(dsOutComInfo.GetColumn(dsOutComInfo.rowposition, 'INH_TAX_RATE'));
}

function fcSearchLastTms()
{	
    var sServiceName = "e07020065-service:searchLastPayTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastTms=dsLastTms";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD",    CURR_USER_MBR_CD);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
function dsLastTms_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (dsLastTms.rowcount() > 0) {
		cboPayMonth.Value = dsLastTms.GetColumn(0, "TMS");
	}
}
