function btnSendMail_OnClick(obj)
{
	var nRow = dsOutMonthPayBac.FindRow("PAY_MONTH", cboPayMonthSendMail.value);
	if (nRow == -1) return;

	var monthActFinishYn = dsOutMonthPayBac.GetColumn(nRow, 'MONTH_ACT_FINISH_YN');
	var mailSendFlag = dsOutMonthPayBac.GetColumn(nRow, 'MAIL_SEND_FLAG');

	//메일발송요청일자 필수 체크 
	if(!fnc_IsMandatory(mskMailSendReqDt, '메일발송 요청일자')){
		return;
	}

	//메일발송요청일자 오늘날짜 보다 큰지 체크 
	var mailSendReqDt = Replace(mskMailSendReqDt.Value, '-');
    if(ToInteger(mailSendReqDt)-ToInteger(CURR_DT) < 0){
		fnc_Message(SNIS_BOA_G005);
		return;
	}

    //상금마감 여부 체크 
    if(monthActFinishYn=='Y'){
        if ( mailSendFlag =='Y' && fnc_Message(SNIS_BOA_G004, "Y" , cboSendYearSendMail.Value,cboPayMonthSendMail.Value) ) {
			mailSendReq();
		}else{
			mailSendReq();
		}
	}else{
		fnc_Message(SNIS_BOA_G003, , cboSendYearSendMail.Value,cboPayMonthSendMail.Value);
		dsOutMonthPayBac.Reset();
	}
}

function dsOutMonthPayBac_OnRowPosChanged(obj,nOldRow,nRow)
{


}

function mailSendReq(){
	var sServiceName = "e07010030-service:saveMonthPaySendMailReq";
	var sInDataSet   = "";
	var sOutDataSet  = "dsOutMonthPayBac=dsOutMonthPayBac";
	var mailSendReqDt = Replace(mskMailSendReqDt.Value, '-');

	fce_AddParam("STND_YEAR", cboSendYearSendMail.Value);
	fce_AddParam("PAY_MONTH", cboPayMonthSendMail.Value);
	fce_AddParam("MAIL_SEND_REQ_DT", mailSendReqDt);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
