>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsPartCodeListValue.ClearData();
    dsSpecCodeListValue.ClearData();
}
function fc_combo(){
	var sServiceName = "e08010001-service:searchbranchcode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDeptListValue=dsDeptListValue ";  

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}
function fcSearch(obj){
	fcCodeSearch(obj);
}
function fcCodeSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e08010001-service:searchPartCode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPartCodeListValue=dsPartCodeListValue ";
        sOutDataSet += "dsSpecCodeListValue=dsSpecCodeListValue ";    

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
function fcBasSearch(obj){
	var sServiceName = "e08010002-service:searchGoal";
    var sInDataSet   = "";
    var sOutDataSet  = "dsGoalList=dsGoalList ";
        sOutDataSet += "dsRateList=dsRateList "; 
        sOutDataSet += "dsBasList=dsBasList ";
        sOutDataSet += "dsSeq=dsSeq ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);     
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);    
}
function fcSaveCheck(obj) { 

	if ( !fnc_DatasetChangeCheck(dsPartCodeListValue) 
	  && !fnc_DatasetChangeCheck(dsSpecCodeListValue) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsPartCodeListValue;
	sMandatoryColID   = "PART_CD|PART_NM";
	sMandatoryColName = "부문코드|부문명";
	oGrid             = tabMain.tab1.grdPartCd;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsSpecCodeListValue;
	sMandatoryColID   = "CD|CD_NM|USE_YN";
	sMandatoryColName = "코드|관리대상명|사용여부";
	oGrid             = tabMain.tab1.grdSpecCd;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}
function fcSaveCheckBas(){

    if(tabMain.tab3.lblratechk = "N"){
		fnc_Message("비중 신규버튼을 눌러주세요", "N");
		return false;
    }
	if(tabMain.tab3.txtComments.Text == ""){
		fnc_Message("변경사유를 등록해 주십시오!", "N");
		tabMain.tab3.txtComments.SetFocus();
		return false;
    }
    for (var i = 0; i < dsRateList.RowCount();i++){
		if(dsRateList.GetColumn(i,"RATE") == 0){
		   fnc_Message(" 등록되지 않은 비중이 있습니다.\n 확인바랍니다.", "N");
		   return false;
		}
    }
	return true;
}
function fcSave(obj){

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = ""; 
	if (tabMain.TabIndex == 0){

		if ( !fcSaveCheck() ) return;   

		sServiceName = "e08010001-service:saveTqcCode";
		sInDataSet   = "dsPartCodeListValue=dsPartCodeListValue:u ";
		sInDataSet  += "dsSpecCodeListValue=dsSpecCodeListValue:u ";

		sOutDataSet  = "dsPartCodeListValue=dsPartCodeListValue ";
		sOutDataSet  += "dsSpecCodeListValue=dsSpecCodeListValue ";

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
    }else{

		//if ( !fcSaveCheckBas() ) return;   

		sServiceName = "e08010002-service:saveTqcGoalBas";
		sInDataSet   = "dsGoalList=dsGoalList:u ";
		sInDataSet  += "dsRateList=dsRateList:a ";
		sInDataSet  += "dsBasList=dsBasList:u ";

		sOutDataSet  = "dsGoalList=dsGoalList ";
		sOutDataSet  += "dsRateList=dsRateList ";
		sOutDataSet  += "ddsBasList=dsBasList ";

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
    }
}
