/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "partBtnRow" :			
           /*
            if ( fnc_DatasetChangeCheck(dsPartCodeListValue) ) {
                fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
                return;
            }*/
            dsPartCodeListValue.AddRow();                     //Record Add
            tabMain.tab1.grdPartCd.SetFocus();                //입력 첫 항목으로 Focus

			break;
		case "specBtnRow" :
		/*
		    if ( fnc_DatasetChangeCheck(dsSpecCodeListValue) ) {
                fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
                return;
            }
            */
			dsSpecCodeListValue.addRow();						//Record Add	
			var nRow = dsSpecCodeListValue.currow;				//현재 row check
            dsSpecCodeListValue.setColumn(nRow, "DEL_YN", "N"); //사용여부 자동 사용
            tabMain.tab1.grdSpecCd.SetFocus();                  //입력 첫 항목으로 Focus
            break;						
    }
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "partBtnRow" :
			if ( !fnc_DeleteCheck(dsPartCodeListValue, "chk", "1") ) return;
			break;
		case "specBtnRow" :
			if ( !fnc_DeleteCheck(dsSpecCodeListValue, "chk", "1") ) return;
            break;
    }
}
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName == "e08010001-service:searchbranchcode" ) {
        fcCodeSearch();
        return;
	}
	// code조회 완료 후
	if ( sServiceName == "e08010001-service:searchPartCode" ) {
	    fcBasSearch();
        return;
	}
	if ( sServiceName == "e08010002-service:searchGoal" ) {
		tabMain.tab3.cmbSeq.Index = 0;
		tabMain.tab3.txtComments.text = dsSeq.GetColumn(0,"COMMENTS");
		SeqCombo();
		return;
	}
	if ( sServiceName == "e08010001-service:saveTqcCode"){
		return;
	}
	if ( sServiceName == "e08010002-service:saveTqcGoalBas"){
	    //tabMain.tab3.lblratechk.Text = 'N';
		fcBasSearch();
		return;
	}
	if ( sServiceName == "e08010002-service:searchRateSeqCombo"){
	}

}


function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcBasSearch();
}


function tabMain_tab3_cmbSeq_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
   tabMain.tab3.txtComments.text = dsSeq.GetColumn(nNewIndex,"COMMENTS");
   SeqCombo();
}
function SeqCombo(){

	var sServiceName = "e08010002-service:searchRateSeqCombo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRateList=dsRateList ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);   
    fce_AddParam("SEQ", tabMain.tab3.cmbSeq.Value );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);    
}
function tabMain_tab3_btn_new_OnClick(obj)  //미사용
{
    tabMain.tab3.lblratechk.Text = 'Y';
    tabMain.tab3.txtComments.text ="";
	for(var i=0;i<dsRateList.RowCount();i++){
		dsRateList.SetColumn(i,"RATE",0);
	}
}
