/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = 'E08010003';
var sBUTTONLIST   = 'FFFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트
/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FFTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	fce_SetStndYear(dsOutStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboStndYear.Index = 0;

    fcSearch();
   // ldept.Text = gdsUser.GetColumn(0,"TEAM_CD");
}
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdCtlCnt:회차등록리스트|grdCtlCnt:건수리스트";
    fce_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}
function fcSearch(){

	var sServiceName = "e08010003-service:searchCtlList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCtlList=dsCtlList ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);  
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
function fcCntSearch(tms){

	var sServiceName = "e08010003-service:searchCtlCntList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCtlCntList=dsCtlCntList ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);  
    fce_AddParam("TMS", tms); 
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
function fcSave(){

	var	sServiceName = "e08010003-service:saveTqcCtlCnt";
	var	sInDataSet   = "dsCtlCntList=dsCtlCntList:u ";
	    sInDataSet   += "dsCtlList=dsCtlList:u ";
	var	sOutDataSet  = "dsCtlCntList=dsCtlCntList ";
	    sOutDataSet  += "dsCtlList=dsCtlList ";
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}
/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName == "e08010003-service:searchCtlList" ) {
	    fcCntSearch(cboTms.Value);
        return;
	}
	if ( sServiceName == "e08010003-service:searchCtlCntList" ) {
		lblDetl.Text = "[ "+cboStndYear.Value+ "년도 "+cboTms.Value+"회차 ]";
        return;
	}
	if ( sServiceName == "e08010003-service:saveTqcCtlCnt"){
		return;
	}

}


function grdCtlList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fcEditableCheck(dsCtlList.GetColumn(nRow,"IST_YN"));

	var tms = dsCtlList.GetColumn(nRow,"TMS");
	cbotms.Text = tms;
	fcCntSearch(tms);
}
function cboTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcEditableCheck(dsCtlList.GetColumn(nNewIndex,"IST_YN"));

	fcCntSearch(strText);
}

function fcPrint(){

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                    
	var	sRptNm    = "e08010003.jrf";               //레포트 파일명
	var sDataSet  = "dsCtlCntList=dsCtlCntList";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg     += " TMS="+cboTms.Value;
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
function fcEditableCheck(chk){
  if(chk == 'Y'){
	grdCtlCnt.Editable = false;
  }else{
    grdCtlCnt.Editable = true;
  }
}
