/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FFFFTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	tabMain.TabIndex = 0;

	fce_SetStndYear(dsOutStndYear, toInteger(fce_GetSysdate("YYYY")));

    cboStndYear.Index = 0;

    fcSearch();

}
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdCtlCnt:회차등록리스트";
    fce_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}
function fcSearch(){

	var sServiceName = "e08010004-service:searchCtlTmsList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCtlCntList=dsCtlCntList ";
		sOutDataSet += "dsCtlCntListPrt=dsCtlCntListPrt ";
		sOutDataSet += "dsCtlQtrCntList=dsCtlQtrCntList ";
		sOutDataSet += "dsCtlQtrCntListPrt=dsCtlQtrCntListPrt ";

    fce_AddParam("STND_YEAR", cboStndYear.Value);  
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "e08010001-service:searchPartCode" ) {

        return;
	}
	if ( sServiceName == "e08010004-service:searchCtlTmsList" ) {

        return;
	}


}

function tabMain_tab1_grdCtlCnt_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2){
		var sCd = dsCtlCntListPivot.GetColumn(nRow, "CD");
		var sCd_nm = dsCtlCntListPivot.GetColumn(nRow, "CD_NM");   //확인필요
		if ( fnc_IsNull(sCd_nm) ) return;

		dsCtlCntListPrt.Filter("CD= '"+sCd+"'");
		dsCtlCntListPrt.Sort("TMS,STND_YEAR");
		var sArg = "Arg_sCd=" + quote(sCd) + " Arg_sCd_nm=" + quote(sCd_nm) + " Arg_Gbn=1";
		Dialog("form::E08010005.xml", sArg, 0, 0, true,0,0);
	}else{
	    var sCd_nm = dsCtlCntListPivot.GetColumn(nRow, "CD_NM");
	    var sContents = dsCtlCntListPivot.GetColumn(nRow, "CONTENTS");
	    var sArg = "Arg_sCd_nm=" + quote(sCd_nm) + " Arg_Cont=" + quote(sContents);
		Dialog("form::E08010004_1.xml", sArg, 0, 0, "");
	}
}

function tabMain_tab2_grdQtrCtlCnt_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2){
		var sCd = dsCtlQtrCntListPivot.GetColumn(nRow, "CD");
		var sCd_nm = dsCtlQtrCntListPivot.GetColumn(nRow, "CD_NM");   

		if ( fnc_IsNull(sCd_nm) ) return;
		dsCtlQtrCntListPrt.Filter("CD= '"+sCd+"'");
		dsCtlQtrCntListPrt.Sort("SEASON,STND_YEAR");
		var sArg = "Arg_sCd=" + quote(sCd) + " Arg_sCd_nm=" + quote(sCd_nm) + " Arg_Gbn=2";
		Dialog("form::E08010005.xml", sArg, 0, 0, true,0,0);
	}else{
	    var sCd_nm = dsCtlCntListPivot.GetColumn(nRow, "CD_NM");
	    var sContents = dsCtlCntListPivot.GetColumn(nRow, "CONTENTS");
	    var sArg = "Arg_sCd_nm=" + quote(sCd_nm) + " Arg_Cont=" + quote(sContents);
		Dialog("form::E08010004_1.xml", sArg, 0, 0, "");
	}
}
function fcPrint(){

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
    var sRptNm    = "";
    var sDataSet  = "";
    if (tabMain.TabIndex = 0){
		sRptNm    = "e08010004_1.jrf";               //레포트 파일명
	    sDataSet  = "dsCtlCntList=dsCtlCntList";    //"UBI 쿼리명, MIP Dataset명"
	}else{
		sRptNm    = "e08010004_2.jrf";               //레포트 파일명
	    sDataSet  = "dsCtlQtrCntList=dsCtlQtrCntList";    //"UBI 쿼리명, MIP Dataset명"
	}
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)	
		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
