#include "lib::boa_common_script.js";
#include "lib::boa_app_script.js";

/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = 'E08010005';
var sBUTTONLIST   = 'FTFFTT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FFFFFT';

 	//화면환경 설정
	sFORMCAPTION  = Arg_sCd_nm +" "+obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	fcGraph();

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
//화면 종료
	if ( Arg_Gbn == 1 or Arg_Gbn == 2)  {
		dsCtlCntListPrt.UnFilter();
		dsCtlQtrCntListPrt.UnFilter();	
	}else{
		dsCtlPointListPrt.UnFilter();
		dsCtlQtrPointListPrt.UnFilter();
	}
	Close();
 }
function fcGraph(){

    uCntGrp.ServletURL1 = "";
    uCntGrp.servletURL2 = "";
    uCntGrp.setJrfFileDir(GBL_REPORT_URL);
    if (Arg_Gbn == 1){
		uCntGrp.setJrfFileName("e08010005_1.jrf");
		uCntGrp.setdataset("dsCtlCntListPrt", "dsCtlCntListPrt");
		uCntGrp.Retrieve();
    }else if (Arg_Gbn == 2){
		uCntGrp.setJrfFileName("e08010005_2.jrf");
		uCntGrp.setdataset("dsCtlQtrCntListPrt", "dsCtlQtrCntListPrt");
		uCntGrp.Retrieve();
    }else if (Arg_Gbn == 3){
		uCntGrp.setJrfFileName("e08010005_3.jrf");
		uCntGrp.setdataset("dsCtlPointListPrt", "dsCtlPointListPrt");
		uCntGrp.Retrieve();
    }else{
		uCntGrp.setJrfFileName("e08010005_4.jrf");
		uCntGrp.setdataset("dsCtlQtrPointListPrt", "dsCtlQtrPointListPrt");
		uCntGrp.Retrieve();
    }
}
