/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {	
	edSEmpNm.Text        = "";
	edSDeptCd.Text       = "";
	edSDeptNm.Text       = "";
	rdoSLucalSocal.Index = 0;
	cboSRelgnCd.Index    = 0;
	calSBirthDt.Value    = "";

    dsOutEmp.ClearData();
    dsOutFmly.ClearData();
    dsOutSchlCarr.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    dsOutEmp.ClearData();
    dsOutFmly.ClearData();
    dsOutSchlCarr.ClearData();

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "zasmp04-service:searchEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEmp=ds_emp";

	fce_AddParam(Trim("EMP_NM         "), edSEmpNm.Text       );
	fce_AddParam(Trim("DEPT_CD        "), edSDeptCd.Text      );
	fce_AddParam(Trim("LUCAL_SOCAL_TPE"), rdoSLucalSocal.Value);
	fce_AddParam(Trim("RELGN_CD       "), cboSRelgnCd.Value   );
	fce_AddParam(Trim("BIRTH_DT       "), calSBirthDt.Value   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEmp     ) 
	  && !fnc_DatasetChangeCheck(dsOutFmly    ) 
	  && !fnc_DatasetChangeCheck(dsOutSchlCarr) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutFmly;
	sMandatoryColID   = "FMLY_NM||RELN_CD||RES_NO";
	sMandatoryColName = "성명||관계||주민번호";
	oGrid             = tbDetail.tb0.grdFmly;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    // 주민등록번호 Check
    var sColumnID = "RES_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 필드값에 값이 없는 경우 메세지를 호출 한 후 해당 Row번호를 return 한다.
			if ( !fnc_ResidenceNumberCheck(oDataSet.GetColumn(i, sColumnID)) ) {
				fnc_Message(SNIS_BOA_0002, "N", i + 1, "주민번호");
				oDataSet.row = i;
			    oGrid.SetFocus();
				oGrid.SetCellPos(oGrid.GetBindCellIndex("body", sColumnID));
				return false;
			}
        }
    }

	// 필수항목체크
	oDataSet          = dsOutSchlCarr;
	sMandatoryColID   = "SCH_NM||SCHL_CARR_CLS_CD";
	sMandatoryColName = "학교명||학교구분";
	oGrid             = tbDetail.tb1.grdSchlCarr;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "zasmp04-service:save";
    var sInDataSet   = "inputFmly=dsOutFmly:u inputSchlCarr=dsOutSchlCarr:u";
    var sOutDataSet  = "";

	fce_AddParam(Trim("EMP_NO         "), dsOutEmp.GetColumn(dsOutEmp.currow, "EMP_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

