/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowDetail" :
		    if ( tbDetail.TabIndex == 0 ) {

				dsOutFmly.AddColumn(Trim("EMP_NO         "));
				dsOutFmly.AddColumn(Trim("FMLY_SEQ       "));
				dsOutFmly.AddColumn(Trim("FMLY_NM        "));
				dsOutFmly.AddColumn(Trim("RELN_CD        "));
				dsOutFmly.AddColumn(Trim("RES_NO         "));
				dsOutFmly.AddColumn(Trim("BIRTH_DT       "));
				dsOutFmly.AddColumn(Trim("LUCAL_SOCAL_TPE"));
				dsOutFmly.AddColumn(Trim("SCHL_CARR      "));
				dsOutFmly.AddColumn(Trim("JOB_CD         "));
				dsOutFmly.AddColumn(Trim("REG_DT         "));
				dsOutFmly.AddColumn(Trim("REG_EMP_NO     "));
				dsOutFmly.AddColumn(Trim("UPDT_DT        "));
				dsOutFmly.AddColumn(Trim("UPDT_EMP_NO    "));

				dsOutFmly.AddRow();                    //Record Add
				dsOutFmly.SetColumn(dsOutFmly.currow, "EMP_NO", dsOutEmp.GetColumn(dsOutEmp.currow, "EMP_NO"));

				tbDetail.tb0.grdFmly.SetFocus();                    //입력 첫 항목으로 Focus
		    }

		    if ( tbDetail.TabIndex == 1 ) {
				dsOutSchlCarr.AddColumn(Trim("EMP_NO          "));
				dsOutSchlCarr.AddColumn(Trim("SCHL_CARR_SEQ   "));
				dsOutSchlCarr.AddColumn(Trim("SCHL_CARR_CLS_CD"));
				dsOutSchlCarr.AddColumn(Trim("SCH_NM          "));
				dsOutSchlCarr.AddColumn(Trim("STRT_DT         "));
				dsOutSchlCarr.AddColumn(Trim("END_DT          "));
				dsOutSchlCarr.AddColumn(Trim("RMK             "));
				dsOutSchlCarr.AddColumn(Trim("REG_DT          "));
				dsOutSchlCarr.AddColumn(Trim("REG_EMP_NO      "));
				dsOutSchlCarr.AddColumn(Trim("UPDT_DT         "));
				dsOutSchlCarr.AddColumn(Trim("UPDT_EMP_NO     "));

				dsOutSchlCarr.AddRow();                    //Record Add
				dsOutSchlCarr.SetColumn(dsOutSchlCarr.currow, "EMP_NO", dsOutEmp.GetColumn(dsOutEmp.currow, "EMP_NO"));

				tbDetail.tb1.grdSchlCarr.SetFocus();                    //입력 첫 항목으로 Focus
		    }

			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowDetail" :
		    if ( tbDetail.TabIndex == 0 ) {
				//삭제 할 자료가 있는지 여부를 체크
				if ( dsOutFmly.RowCount() > 0 ) {
					if ( fnc_DeleteCheck(dsOutFmly, "FMLY_NM") ) dsOutFmly.DeleteSelected();        //삭제 여부 확인
				} else {
					fnc_Message(SNIS_COM_1003, "N");                      //삭제 자료 없음 정보 Display
				}

				tbDetail.tb0.grdFmly.SetFocus();                          //Record 삭제 후 Grid로 Focus
			}

		    if ( tbDetail.TabIndex == 1 ) {
				//삭제 할 자료가 있는지 여부를 체크
				if ( dsOutSchlCarr.RowCount() > 0 ) {
					if ( fnc_DeleteCheck(dsOutSchlCarr, "SCH_NM") ) dsOutSchlCarr.DeleteSelected();        //삭제 여부 확인
				} else {
					fnc_Message(SNIS_COM_1003, "N");                      //삭제 자료 없음 정보 Display
				}

				tbDetail.tb1.grdSchlCarr.SetFocus();                      //Record 삭제 후 Grid로 Focus
			}

			break;
	}
}

