/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

	var sFormName   = "";    //호출할 Form ID
	var sInputValue = "";    //입력된 자료가 있다면 Default로 조회 
    var sResponse;           //Popup에서 받을 결과 값

	switch(obj.id) {
		case "imgSDept" :                   // Dept Popup
			sSearchKind = "DPTR";
			sInputValue = "";
			sResponse = fce_HelpDialoge(sSearchKind, sInputValue, "");  // Popup Open
			if ( sResponse != 0 ) {
				edSDeptCd.Text  = sResponse[0];          //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
				edSDeptNm.Text  = sResponse[1];          //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
			}
			break;

		case "imgDept" :                   // Dept Popup
			sSearchKind = "DPTR";
			sInputValue = "";
			sResponse = fce_HelpDialoge(sSearchKind, sInputValue, "");  // Popup Open
			if ( sResponse != 0 ) {
				edDeptCd.Text  = sResponse[0];          //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
				edDeptNm.Text  = sResponse[1];          //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
			}
			break;

		case "imgPost" :   //우편번호 조회
			sSearchKind = "POST";                    //우편번호 조회 POPUP
			sInputValue = "";                        //검색 조건 Text
			sCommonGubn = "";                        //공통 구분 코드(공통코드 조회가 아니면 null)
			sResponse   = fce_HelpDialoge(sSearchKind,sInputValue,"");  // Popup Open

			if ( sResponse != 0 ) {
				mskPostNo.value = sResponse[0];    //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
				edAdd1Home.Text = sResponse[1];    //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
			}
			break;

		case "imgPhoto" :   //사진
			sResponse = fce_GetSelectFile();
			if ( !fnc_IsNull(sResponse) ) {
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_UPDATE", "Y"      );
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_PATH"  , sResponse[0]);
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_NM"    , sResponse[1]);
			}
			break;	
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){

	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	var sSearchKind = "";
	var sInputValue = "";
    var sResponse;                                              //Popup에서 받을 결과 값
	switch (sColIdx){
		case "부서" :
			sSearchKind = "DPTR";
			sInputValue = "";
			sResponse = fce_HelpDialoge(sSearchKind, sInputValue, "");  // Popup Open
			if ( sResponse != 0 ) {
				dsOutEmp.SetColumn(dsOutEmp.currow, "DEPT_CD", sResponse[0]);
				dsOutEmp.SetColumn(dsOutEmp.currow, "DEPT_NM", sResponse[1]);
			}
			break;
		case "우편번호" :
			sSearchKind = "POST";                                         //우편번호 조회 POPUP
			sInputValue = "";                                             //검색 조건 Text
			sResponse   = fce_HelpDialoge(sSearchKind, sInputValue, "");  // Popup Open
			if (length(trim(sResponse))!=0) {
				dsOutEmp.SetColumn(dsOutEmp.currow, "POST_HOME", sResponse[0]);
				dsOutEmp.SetColumn(dsOutEmp.currow, "ADD1_HOME", sResponse[1]);
			}
			break;

		case "생년월일" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;

		case "입학일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;

		case "졸업일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;

		case "사진" :
			sResponse = fce_GetSelectFile();
			if ( !fnc_IsNull(sResponse) ) {
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_UPDATE", "Y"      );
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_PATH"  , sResponse[0]);
				dsOutEmp.SetColumn(dsOutEmp.currow, "PTO_NM"    , sResponse[1]);
			}
			break;	
		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "com-code-service:search" ) {
		// 코드 추가
		fce_AddCodeRow(dsOutSRelgn, "전체", "", cboSRelgnCd);
	}

	// 사원 조회 후
	if ( sServiceName == "zasmp04-service:searchEmp" ) {
        fnc_Message(SNIS_BOA_0003, "N", dsOutEmp.GetRowCount());
	}

	// 사원 조회 후
	if ( sServiceName == "zasmp04-service:searchDetail" ) {
        fnc_Message(SNIS_BOA_0003, "N", dsOutFmly.GetRowCount() + dsOutSchlCarr.GetRowCount());
	}

	// 사원 저장 후
	if ( sServiceName == "zasmp04-service:save" ) {
		fcSearchDetail();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	if ( sServiceName == "dept-service:searchDept" ) {
		//코드명 조회 후 처리
		fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
	} 
}

/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
    //변화가 없으면 굳이 실행 하지 않는다.
    if ( obj.value == obj.userdata ) return;

	// 값이 없으면 종료
	var sInputValue  = trim(obj.value);      //검색 할 자료 
	if ( fnc_IsNull(sInputValue) ) return;

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = object(obj.id);
	sGRDTIT  = "";

	switch(obj.id) {
		case "edSDeptCd" :                     //부서 코드 
			vREVOBJ[0]   = "edSDeptCd";        //부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edSDeptNm";        //부서 명을 받을 Text Object명
			sGRDTIT      = "부서코드:부서명";  //그리드 제목

			sServiceName = "dept-service:searchDept";
			sInDataSet   = "";                               //자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=ds_dept";             //조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("DEPT_CD"), Trim(edSDeptCd.Text));
			fce_AddParam(Trim("DEPT_NM"), Trim(edSDeptNm.Text));
			break;
		case "edSDeptNm" :                     //부서 코드 
			vREVOBJ[0]   = "edSDeptCd";        //부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edSDeptNm";        //부서 명을 받을 Text Object명
			sGRDTIT      = "부서코드:부서명";  //그리드 제목

			sServiceName = "dept-service:searchDept";
			sInDataSet   = "";                               //자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=ds_dept";             //조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("DEPT_CD"), Trim(edSDeptCd.Text));
			fce_AddParam(Trim("DEPT_NM"), Trim(edSDeptNm.Text));
			break;

		case "edDeptCd" :                     //부서 코드 
			vREVOBJ[0]   = "edDeptCd";        //부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edDeptNm";        //부서 명을 받을 Text Object명
			sGRDTIT      = "부서코드:부서명";  //그리드 제목

			sServiceName = "dept-service:searchDept";
			sInDataSet   = "";                               //자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=ds_dept";             //조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("DEPT_CD"), Trim(edDeptCd.Text));
			fce_AddParam(Trim("DEPT_NM"), Trim(edDeptNm.Text));
			break;

		case "edDeptNm" :                     //부서 코드 
			vREVOBJ[0]   = "edDeptCd";        //부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edDeptNm";        //부서 명을 받을 Text Object명
			sGRDTIT      = "부서코드:부서명";  //그리드 제목

			sServiceName = "dept-service:searchDept";
			sInDataSet   = "";                               //자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=ds_dept";             //조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("DEPT_CD"), Trim(edDeptCd.Text));
			fce_AddParam(Trim("DEPT_NM"), Trim(edDeptNm.Text));
			break;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  학력, 가족사항 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchDetail() 
{
	//dsOutFmly.ClearData();
    if ( dsOutEmp.GetRowCount() < 0 ) return;
    if ( fnc_IsNull(dsOutEmp.GetColumn(dsOutEmp.currow, "EMP_NO")) ) return;

	var sServiceName = "zasmp04-service:searchDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutFmly=ds_fmly dsOutSchlCarr=ds_schl_carr";

	fce_AddParam(Trim("EMP_NO         "), dsOutEmp.GetColumn(dsOutEmp.currow, "EMP_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  Dataset 입력값 정합성 체크
+-------------------------------------------------------------------------------------------------*/
function tbDetail_tb0_grdFmly_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	var oDataset = dsOutFmly;

	var sColIdx  = obj.GetCellText("HEAD", nOldRow, nOldCell);  //해당 HEAD명 
	switch( sColIdx ) {
		case "주민번호":
			var sResNo = oDataset.GetColumn(nOldRow, "RES_NO");
			if ( fnc_IsNull(sResNo) ) break;
			if ( fnc_ResidenceNumberCheck(sResNo) == false ) {
				fnc_Message(SNIS_COM_1034, "N", "주민번호");
				obj.SetFocus();
				oDataset.row = nOldRow;
				obj.SetCellPos(nOldCell);
			}
			break;
		default:
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  사원선택 변경시
+-------------------------------------------------------------------------------------------------*/
function dsOutEmp_OnRowPosChanged(obj,nOldRow,nRow)
{
	if ( fnc_DatasetChangeCheck(dsOutFmly    ) 
	  || fnc_DatasetChangeCheck(dsOutSchlCarr) ) {
		//저장 할 자료가 있음
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
		    dsOutEmp.FireEvent = false;
			dsOutEmp.row = nOldRow;
		    dsOutEmp.FireEvent = true;
			fcSave();
			return;
		}
	}

	fcSearchDetail();
}
