Window.Script:
var str_btn1 ="";
var str_btn2 ="";
var str_btn3 ="";


function form_OnLoadCompleted(obj)
{
	form.Title = title;
	Static0.Text = str_msg;
	switch(btn_style)
	{
		case "MB_ABORTRETRYIGNORE":
			Button0.text = "중단";
			Button1.text = "다시시도";
			Button2.text = "무시";
			fn_BtnPos(3);
		break;

		case "MB_OK":
			Button0.text = "확인";
			fn_BtnPos(1);
		break;

		case "MB_OKCANCEL":
			Button0.text = "확인";
			Button1.text = "취소";
			fn_BtnPos(2);
		break;

		case "MB_RETRYCANCEL":
			Button0.text = "다시시도";
			Button1.text = "취소";
			fn_BtnPos(2);
		break;

		case "MB_YESNOCANCEL":
			Button0.text = "예";
			Button1.text = "아니오";
			Button2.text = "취소";
			fn_BtnPos(3);
		break;
		case "MB_HONGJO":
			Button0.text = "실행";
			Button1.text = "다운로드";
			Button2.text = "취소";
			fn_BtnPos(3);
		break;
	}
}

function fn_BtnPos(num)
{
	switch(num)
	{
		case 1:
		Button1.Visible = false;
		Button2.Visible = false;
		Button0.Left = 88;
		break;

		case 2:
		Button2.Visible = false;
		Button0.Left = 46;
		Button1.Left = 128;
		break;

	}
}


function Close_Return_Clik(obj)
{
	var rtn_val;
	switch(obj.text)
	{
		case "확인":
			rtn_val = 1;
		break;

		case "취소":
			rtn_val = 2;
		break;

		case "중단":
			rtn_val = 3;
		break;

		case "다시시도":
			rtn_val = 4;
		break;

		case "무시":
			rtn_val = 5;
		break;

		case "예":
			rtn_val = 6;
		break;

		case "아니오":
			rtn_val = 7;
		break;
		case "실행":
			rtn_val = 8;
		break;
		case "다운로드":
			rtn_val = 9;
		break;
	}
	close(rtn_val);
}
