var sFile;						// 저장될 xls/csv 파일명
var sFileType = "EXCEL";		// 0:xls, 1:csv
var objGrdExcel;				// 부모폼의 Grid 객체
var arrGrdExcel=array(-1);		// 부모폼의 Grid 객체 array
var nStartIdx = 0;				// 엑셀 저장할 Grid의 시작셀 index
var nMaxExcelRowCnt = 65530;	// 5530;65534;65536

/***************************************************************************************************
*                                         FORM Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormOnLoadCompleted(obj)
{
    if(isExistVar("objGridId")) 
    {		
		var arrGrdExcel	= split2(objGridId,"|",":");

		for(var i=0;i<arrGrdExcel.length;i++)
		{
			arrGrdExcel[i] = parent.Object(arrGrdExcel[i][0]);
			if(isValid(arrGrdExcel[i])) 
			{

				var objDsExcel = parent.Object(arrGrdExcel[i].BindDataset);

				if(!isValid(objDsExcel)) {
					alert("존재하지 않는 Dataset : " + objGrdExcel.BindDataset);
					close(false);
				} else {
					ds_excelComm.AppendDataset(objDsExcel);
				}				
			}
			else
			{
				alert("존재하지 않는 Grid : " + arrGrdExcel[i]);
				close(false);
			}

		}

    } else {
        alert("해당 그리드가 존재하지 않습니다");
        close(false);
    }

	// 파일 이름 설정
	if(isExistVar("sFileNm")) 
    {
		ed_fileNm.Text = "c:\\"+ sFileNm+".xls";        
		fd_excel.FileName= sFileNm+".xls";
    }

	if(isExistVar("sExcYn")) 
    {
        chk_run.Enable = sExcYn;        
    }


}




