*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 다운로드용 멀티 Grid Content 만들기!
+-------------------------------------------------------------------------------------------------*/
function fcSetMultiGridContent(sGridId,sFile)
{

	var arrGrdExcel	= split2(sGridId,"|",":");
    var arrDsExcel	= array(arrGrdExcel.length);
    var arrGrdRowBlank	= array(arrGrdExcel.length);

    var nGrdFail = 0;
    var nDsFail =0;

    for(var i=0;i<arrGrdExcel.length;i++)
    {

		arrGrdRowBlank[i]=parseInt(arrGrdExcel[i][1]);	// 그리드 사이 설정값
		arrGrdExcel[i] = Object(arrGrdExcel[i][0]);		
		arrDsExcel[i] = Object(arrGrdExcel[i].BindDataset);		
    }

	var ExportObject1;
	var sXML;
	var arrCnt = array(-1);		

	arrCnt[0] = 1; // 두번째 열부터 시작됨

	var nCnt = 1;
	for(var i=0;i<arrDsExcel.length;i++)
	{
		arrCnt[i+1] = arrCnt[i] + arrDsExcel[i].rowcount + arrGrdRowBlank[i];				
	}

	ExportObject1 = CreateExportObject();
	ExportObject1.ExportType		= "Excel";

	ExportObject1.ExportFileName	= sFile;
	ExportObject1.ActiveSheetName   =  sFileNm;	
	ExportObject1.MakeEmptyFileWhenNotExist = true;			


	for(var i=0;i<arrGrdExcel.length;i++)
    {

		/*
		var grdExcelObj =  fcdSetGridContent(arrGrdExcel[i],0);	

		grdExcelObj.UseAutoSizing ="Col_Merge";		
		grdExcelObj =  fcdSetGridContent(grdExcelObj,0);		*/


		//arrGrdExcel[i].UseAutoSizing ="Col_NoHead_Merge";
		ExportObject1.AddExportGrid( sFileNm+ "!" + "A"+arrCnt[i]	, arrGrdExcel[i], true, false);


    }


	ExportObject1.Export();

	//
	ExportObject1.Save();

	ExportObject1.Close();
	ExportObject1 = null;


}
