/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsGrpCodeListValue.ClearData();
    dsSpecCodeListValue.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    edSearchTxt.Text = Trim(edSearchTxt.Text);
	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e01010010-service:searchGrpCode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsGrpCodeListValue=dsGrpCodeListValue ";
        sOutDataSet += "dsSpecCodeListValue=dsSpecCodeListValue ";    

	fce_AddParam("DEL_YN", "N"             );
	fce_AddParam("GRP_NM", edSearchTxt.text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcGrpCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if ( strColumnID == "GRP_CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "그룹코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "그룹코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

function fcSpecCodeExistsCheck(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if ( strColumnID == "CD" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "코드가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}


function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsGrpCodeListValue) 
	  && !fnc_DatasetChangeCheck(dsSpecCodeListValue) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsGrpCodeListValue;
	sMandatoryColID   = "GRP_CD|GRP_NM";
	sMandatoryColName = "그룹코드|그룹코드명";
	oGrid             = grdGrpCd;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsSpecCodeListValue;
	sMandatoryColID   = "GRP_CD|CD|CD_NM";
	sMandatoryColName = "그룹코드|코드|코드명";
	oGrid             = grdSpecCd;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "e01010010-service:saveCode";
    sInDataSet   = "dsGrpCodeListValue=dsGrpCodeListValue:u ";
    sInDataSet  += "dsSpecCodeListValue=dsSpecCodeListValue:u ";

    sOutDataSet  = "dsGrpCodeListValue=dsGrpCodeListValue ";

	fce_AddParam("DEL_YN", "N"); 
	fce_AddParam("GRP_NM", edSearchTxt.text);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


