/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "e01010010-service:searchGrpCode" ) {
        // chk 컬럼 추가, pageSetting
        dsGrpCodeListValue.AddColumn("CHK");
        return;
	}

	if ( sServiceName == "e01010010-service:searchSpecCode" ) {
        // chk 컬럼 추가
        dsSpecCodeListValue.AddColumn("CHK");
        return;
	}

	// 저장 완료 후
	if ( sServiceName == "e01010010-service:saveCode" ) {
        return;
	}
}

