/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :			
            if ( fnc_DatasetChangeCheck(dsGrpCodeListValue) ) {
                fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
                return;
            }
            dsGrpCodeListValue.AddRow();                     //Record Add
            grdGrpCd.SetFocus();         //입력 첫 항목으로 Focus

			break;
		case "specBtnRow" :
            if ( fnc_IsNull(dsGrpCodeListValue.getColumn(dsGrpCodeListValue.row, "GRP_CD")) ) {
                fnc_Message(L_COM_ALT_0008, "N", "그룹코드");
                grdGrpCd.SetFocus();
                grdGrpCd.SetCellPos(grdGrpCd.GetBindCellIndex("body", "GRP_CD"));
                return;
            }

			dsSpecCodeListValue.addRow();
			var nRow = dsSpecCodeListValue.currow;
			dsSpecCodeListValue.setColumn(nRow, "GRP_CD", dsGrpCodeListValue.getColumn(dsGrpCodeListValue.row, "GRP_CD"));
            dsSpecCodeListValue.SetColumn(nRow, "GRP_NM", dsGrpCodeListValue.GetColumn(dsGrpCodeListValue.row, "GRP_NM"));
            dsSpecCodeListValue.setColumn(nRow, "DEL_YN", "N");
            break;						
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( !fnc_DeleteCheck(dsGrpCodeListValue, "chk", "1") ) return;
			dsGrpCodeListValue_OnRowPosChanged();
			break;
		case "specBtnRow" :
			if ( !fnc_DeleteCheck(dsSpecCodeListValue, "chk", "1") ) return;
            break;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsGrpCodeListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
    dsSpecCodeListValue.ClearData();
	fcGetSpecCode();
}

/*-------------------------------------------------------------------------------------------------+
>>  상세코드 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetSpecCode() {
    var sServiceName = "e01010010-service:searchSpecCode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSpecCodeListValue=dsSpecCodeListValue";

    fce_AddParam("GRP_CD", dsGrpCodeListValue.getColumn(dsGrpCodeListValue.row, "GRP_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");       
}
