function fcSearch(currentPage) {

    if(currentPage > 999999) currentPage = 1;
	var sServiceName = "e01010020-service:searchUser";
    var sInDataSet   = "";
    var sOutDataSet  = "dsUserListValue=dsUserListValue ";
        sOutDataSet += "dsCountValue=dsCountValue ";    

	fce_AddParam(Trim("ROWNUM1"), currentPage  ); 
    fce_AddParam(Trim("ROWNUM3"), currentPage  ); 

	fce_AddParam(Trim("ROWNUM2"), sPAGENUM   ); 
	fce_AddParam(Trim("ROWNUM4"), sPAGENUM   ); 

	var searchKey = "";
	fce_AddParam("USER_NM", edSearchTxt.text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsUserListValue) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsUserListValue;
	sMandatoryColID   = "USER_ID|USER_NM|DEPT_CD|PSWD|DUTY_GRP_CD|FLOC_CD|HP_NO|STR_DT|END_DT";
	sMandatoryColName = "사용자ID|사용자명|부서|비밀번호|프로그램그룹|직위|핸드폰번호|시작일|종료일";
	oGrid             = grdUserList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    return true;
}



function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    var sServiceName = "e01010020-service:saveUser";
    var sInDataSet   = "dsUserListValue=dsUserListValue:u";
    var sOutDataSet  = "dsUserListValue=dsUserListValue ";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


