/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch("1");
	}	

	// 조회 완료 후
	if ( sServiceName == "e01010020-service:searchUser" ) {
        dsUserListValue.AddColumn("CHK");

        // pagesetting
        fcSetPageCombo();
        fcUserDetail("0");
	}

    // ID 중복체크
    if ( sServiceName == "e01010020-service:searchIdCheck" ) {
        if(fnc_isNull(dsIdCheckValue.getColumn(0, "USER_ID"))) {
            fnc_Message(SNIS_BOA_0007, "N");

            edPasswd    .Readonly   = false;
            edUserNm    .Readonly   = false;
            cbDutyGrp   .Enable     = true;
            cbDept      .Enable     = true;
            cbFloc      .Enable     = true;
            edTelNo     .Readonly   = false;
            edHpNo      .Readonly   = false;
            cbMbr       .Enable     = true;
            calStrDt    .Enable     = true;
            calEndDt    .Enable     = true;

            edPasswd.SetFocus();
            fnc_SetBKColor(this);


        } else {
            fnc_Message(L_COM_ALT_0016, "N", "사용자ID가");
            edUserId.text = "";
            edUserId.SetFocus();
        }
    }   
}

