/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "divBtnRow" :
            var sArg = "";

            btnIdChk.visible = true;
			dsUserListValue.AddRow();         //Record Add

            edUserId.SetFocus();

            edPasswd    .Readonly   = true;
            edUserNm    .Readonly   = true;
            cbDutyGrp   .Enable     = false;
            cbDept      .Enable     = false;
            cbFloc      .Enable     = false;
            edTelNo     .Readonly   = true;
            edHpNo      .Readonly   = true;
            cbMbr       .Enable     = false;
            calStrDt    .Enable     = false;
            calEndDt    .Enable     = false;

            cbMbr.Index = 0;

            fnc_SetBKColor(this);
			break;			
    } // end switch.case
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    var sArg = "";

    if ( !fnc_DeleteCheck(dsUserListValue, "chk", "1") ) return;

    if ( dsUserListValue.GetRowCount() <= dsUserListValue.currow ) {
        dsUserListValue.FireEvent = false;
        dsUserListValue.row = dsUserListValue.GetRowCount() - 1;
        dsUserListValue.SelectRow(dsUserListValue.GetRowCount() - 1);
        dsUserListValue.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsUserListValue) ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsUserListValue.FireEvent = false;
            dsUserListValue.row = nOldRow;
			fcSave();
            dsUserListValue.FireEvent = true;
			return;
		}
	}

    fcSearch("1");  
}


/*-------------------------------------------------------------------------------------------------+
>>  ID 중복체크 선작업
+-------------------------------------------------------------------------------------------------*/
function fcIdCheckBefore(obj) {
    if(length(edUserId.Text) > 5) {
        if(fnc_isNull(edUserId.text)) {
            fnc_Message(SNIS_COM_1034, "N", "사용자ID");
            edUserId.SetFocus();
            return false;
        } 

        fcIdCheck();
    } else {
        fnc_Message(SNIS_BOA_C001, "N", "사용자ID", "6자이상");  
        return false;
    }
}


