function fcUserDetail(sIndex) {
    edUserId.text           =   dsUserListValue.getColumn(sIndex,   "USER_ID");
    edUserId.readonly       =   true;
}


function fcUserDetailBefore(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var clickedRow = obj.GetClickedRow();
    fcUserDetail(clickedRow);  
}




function fcSetPageCombo() {
    var pComboIndex     = 0;
    var nTotalCount     = 0;

    if(cbCountPage.index > -1) pComboIndex = cbCountPage.index;
    dsOutPage.ClearData();
    nTotalCount = ceil(toInteger(dsCountValue.getColumn(0, "TOTCOUNT")) / toInteger(sPageNum));
    if(nTotalCount <= 0) cbCountPage.visible = true;
    else {
        for ( var i = 0; i < nTotalCount; i++ ) {
            dsOutPage.addRow();
            dsOutPage.setColumn(i, "TOTCOUNT", toString(i + toInteger(1)));
        } // end for

        cbCountPage.index = pComboIndex;
    }
}


