function dsUserListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( obj.GetRowType(nRow) == "insert" ) {
        edUserId.Readonly = false;
	} else {
        edUserId.Readonly = true;
	}

    fnc_SetBKColor(this);
}


function edit_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
    var chgStr = chr(nChar);
    if(chgStr >= 0 && chgStr <= 9 || nChar == 45 || nChar == 8) return true;
    else return false;
}


function editStr_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{    
    if (   (nChar >= 33       &&  nChar <= 64)
        || (nChar >= 91       &&  nChar <= 96)        
        || (nChar >= 123      &&  nChar <= 126) ) return false;
    else return true;
}


function calEndDt_OnCloseUp(obj,strOldText,strNewText)
{
	var sDt = calStrDt.Value;
	var eDt = calEndDt.Value;
	if(    !fnc_isNull(sDt) 
        && !fnc_isNull(eDt)
        && toInteger(sDt) >= toInteger(eDt) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작일이 종료일보다 이후일 수");
        calStrDt.Value = "";
        calEndDt.Value = "";
	}	
}
