/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsAuthListValue.ClearData();
    dsUserListValue.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    edSearchTxt.Text = Trim(edSearchTxt.Text);

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e01010040-service:searchUser";
    var sInDataSet   = "";
    var sOutDataSet  = "dsUserListValue=dsUserListValue ";

	fce_AddParam("TEAM_CD", cboTeamNm.Value);
	fce_AddParam("USER_NM", edSearchTxt.text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchAuthMenuListCheck(obj) {
    //dsAuthListValue.ClearData();

	if ( fnc_IsNull(dsUserListValue.GetColumn(dsUserListValue.currow, "USER_ID")) ) return false;
	return true;
}

function fcSearchAuthMenuList(obj) {
    if ( !fcSearchAuthMenuListCheck(obj) ) return;

    var sServiceName = "e01010040-service:searchUserMenu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue ";

    fce_AddParam("USER_ID", dsUserListValue.GetColumn(dsUserListValue.currow, "USER_ID"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");     
}

