function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    var sServiceName = "e01010040-service:saveMenuAuth";
    var sInDataSet   = "dsUserListValue=dsUserListValue:u "
                     + "dsAuthListValue=dsAuthListValue:u ";
    var sOutDataSet  = "dsAuthListValue=dsAuthListValue ";

	fce_AddParam("TEAM_CD", cboTeamNm.Value);
	fce_AddParam("USER_NM", edSearchTxt.text);
    fce_AddParam("USER_ID", dsUserListValue.GetColumn(dsUserListValue.currow, "USER_ID"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

