/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
        case "divBtnRow" :
            dsUserListValue.AddRow();

            var nRow = dsUserListValue.currow;
            dsUserListValue.SetColumn(nRow, "GBN", "002");
            dsUserListValue.SetColumn(nRow, "ASSO_CD", dsOutAsso.GetColumn(0, "ASSO_CD"));
            dsUserListValue.SetColumn(nRow, "ASSO_NM", dsOutAsso.GetColumn(0, "ASSO_NM"));
            dsUserListValue.SetColumn(nRow, "DEPT_CD", dsOutDept.GetColumn(0, "DEPT_CD"));
            dsUserListValue.SetColumn(nRow, "DEPT_NM", dsOutDept.GetColumn(0, "DEPT_NM"));
            dsUserListValue.SetColumn(nRow, "TEAM_CD", dsOutTeam.GetColumn(0, "TEAM_CD"));
            dsUserListValue.SetColumn(nRow, "TEAM_NM", dsOutTeam.GetColumn(0, "TEAM_NM"));

            edUserId.Readonly = false;
            edUserNm.Readonly = false;
            edPswd  .Readonly = false;
            edEMail .Readonly = false;
            edTelNo .Readonly = false;
            edFloc  .Readonly = false;
            edFgrade.Readonly = false;

            cboAsso .Enable   = true;
            cboDept .Enable   = true;
            cboTeam .Enable   = true;

            break;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsUserListValue, "DEL", "1") ) return;

			dsUserListValue_OnRowPosChanged(dsUserListValue,dsUserListValue.currow,dsUserListValue.currow);
			break;  	
    }
}

