/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        var nRow = 0;

        dsOutTeamNm.InsertRow(nRow);
        dsOutTeamNm.SetColumn(nRow, "TEAM_CD", "");
        dsOutTeamNm.SetColumn(nRow, "TEAM_NM", "전체");

        cboTeamNm.index = nRow;
        fcSearch();
        return;
	}

	// 조회 완료 후
	if ( sServiceName == "e01010040-service:searchUser" ) {
        dsUserListValue.AddColumn("CHK");
        dsUserListValue.AddColumn("DEL");
        return;
	}

	// 메뉴조회 완료 후 
	if ( sServiceName == "e01010040-service:searchUserMenu" ) {
        dsAuthListValue.addColumn("CHK");
        return;
    }


    // 저장 완료 후 
	if ( sServiceName == "e01010040-service:saveUserMenu" ) {
        dsAuthListValue.addColumn("CHK");

        return;
    }
}

