function dsAuthListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "CHK" ) {
        var sUpMnId = dsAuthListValue.getColumn(nRow, "UP_MN_ID");

        var sSrchValue = chkSrch.Value;
        var sInptValue = chkInpt.Value;
        if ( varNewValue != 1 ) {
            sSrchValue = 0;
            sInptValue = 0;
        }

        for ( var i = 0; i <= dsAuthListValue.rowcount; i++) {
            if ( dsAuthListValue.getColumn(i, "UP_MN_ID") == sUpMnId ) {
                dsAuthListValue.setColumn(i, "SRCH_YN", sSrchValue);
                dsAuthListValue.setColumn(i, "INPT_YN", sInptValue);
            }
        }
	}

	// 저장 선택시 조회권한도 선택, 조회권한 취소시 저장권한도 취소
	if (strColumnID == "INPT_YN" && varNewValue == "1") {
		if (dsAuthListValue.GetColumn(nRow, "SRCH_YN") <> "1") {
			dsAuthListValue.SetColumn(nRow, "SRCH_YN", "1");
		}
	} else if (strColumnID == "SRCH_YN" && varNewValue == "0") {
		if (dsAuthListValue.GetColumn(nRow, "INPT_YN") <> "0") {
			dsAuthListValue.SetColumn(nRow, "INPT_YN", "0");
		}
	}
}

function dsUserListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( obj.GetColumn(nRow, "GBN") == "002" ) {
        edUserId.Readonly = false;
        edUserNm.Readonly = false;
        edPswd  .Readonly = false;
        edEMail .Readonly = false;
        edTelNo .Readonly = false;
        edFloc  .Readonly = false;
        edFgrade.Readonly = false;

        cboAsso .Enable   = true;
        cboDept .Enable   = true;
        cboTeam .Enable   = true;
    } else {
        edUserId.Readonly = true;
        edUserNm.Readonly = true;
        edPswd  .Readonly = true;
        edEMail .Readonly = true;
        edTelNo .Readonly = true;
        edFloc  .Readonly = true;
        edFgrade.Readonly = true;

        cboAsso .Enable   = false;
        cboDept .Enable   = false;
        cboTeam .Enable   = false;
    }

	fcSearchAuthMenuList(obj);
}

function dsUserListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "USER_ID" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "사용자ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "사용자ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}

	if ( strColumnID == "ASSO_CD" ) {
        obj.SetColumn(nRow, "ASSO_NM", cboAsso.Text);
    }

	if ( strColumnID == "DEPT_CD" ) {
        obj.SetColumn(nRow, "DEPT_NM", cboDept.Text);
    }

	if ( strColumnID == "TEAM_CD" ) {
        obj.SetColumn(nRow, "TEAM_NM", cboTeam.Text);
    }

}

function edSearchTxt_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == '13' && edSearchTxt.Text != "" ) {
		fcSearch();
	}
}

function cboAsso_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsOutDept.Filter("ASSO_CD='"+substr(cboAsso.value,0,4)+"'");
}

function cboDept_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsOutTeam.Filter("DEPT_CD='"+substr(cboDept.value,0,6)+"'");
}
