/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" || obj.id == "cboSStndYear" ) {
		dsOutCfrntMeth.ClearData();
	}
    dsOutRacerArrangeStnd.ClearData();
    dsOutCfrntDay.ClearData();
    dsOutCfrntRace.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    edSCfrntCd.Text = Trim(edSCfrntCd.Text);

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02010020-service:searchCfrntMeth";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCfrntMeth=dsOutCfrntMeth ";

	fce_AddParam(Trim("CFRNT_CD     "), edSCfrntCd.Text   );
	fce_AddParam(Trim("STND_YEAR    "), cboSStndYear.Value );
	fce_AddParam(Trim("RACE_CNT     "), mskSRaceCnt.Value );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  대진코드별 상세 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchDetailCheck() {
    if ( dsOutCfrntMeth.GetRowCount() == 0 ) return false;

	fcInit();

	return true;
}

function fcSearchDetail(obj) {
    if ( !fcSearchDetailCheck() ) return;

	var sServiceName = "e02010020-service:searchRacerArrangeStnd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerArrangeStnd=dsOutRacerArrangeStnd "
                     + "dsOutCfrntDay=dsOutCfrntDay "
                     + "dsOutCfrntRace=dsOutCfrntRace ";

	fce_AddParam(Trim("CFRNT_CD "), dsOutCfrntMeth.GetColumn(dsOutCfrntMeth.currow, "CFRNT_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


