/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowCfrntMeth" :
            var sArg = "Arg_sStndYear=" + quote(cboSStndYear.Value);
            if ( Dialog("form::E02010021.xml", sArg, 834, 450, "") ) fcSearch(divSearch.btnSearch);

/*
            if ( fnc_DatasetChangeCheck(dsOutCfrntMeth) ) {
                alert("변경된 값을 저장 한 후에 작업하시기 바랍니다.");
                return;
            }

			dsOutCfrntMeth.AddRow();         //Record Add
			grdCfrntMeth.SetFocus();         //입력 첫 항목으로 Focus
*/
			break;
		case "divBtnRowRacerArrangeStnd" :
			if ( fnc_IsNull(dsOutCfrntMeth.GetColumn(dsOutCfrntMeth.currow, "CFRNT_CD")) ) {
				fnc_Message(L_COM_ALT_0007, "N", "대진코드");
				return;
			}

			dsOutRacerArrangeStnd.AddRow();         //Record Add
			dsOutRacerArrangeStnd.SetColumn(dsOutRacerArrangeStnd.currow, "CFRNT_CD", dsOutCfrntMeth.GetColumn(dsOutCfrntMeth.currow, "CFRNT_CD"));
			grdRacerArrangeStnd.SetFocus();         //입력 첫 항목으로 Focus
			break;
		case "divBtnRowCfrntDay" :
			if ( fnc_IsNull(dsOutCfrntMeth.GetColumn(dsOutCfrntMeth.currow, "CFRNT_CD")) ) {
				fnc_Message(L_COM_ALT_0007, "N", "대진코드");
				return;
			}

			dsOutCfrntDay.AddRow();         //Record Add
			dsOutCfrntDay.SetColumn(dsOutCfrntDay.currow, "CFRNT_CD", dsOutCfrntMeth.GetColumn(dsOutCfrntMeth.currow, "CFRNT_CD"));

			for ( var i = 0; i < dsOutCfrntDay.GetRowCount(); i++ ) {
				dsOutCfrntDay.SetColumn(i, "DAY_ORD", i + 1);
			}

			grdCfrntDay.SetFocus();         //입력 첫 항목으로 Focus
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowCfrntMeth" :
			if ( !fnc_DeleteCheck(dsOutCfrntMeth, "chk", "1") ) return;
            fcInit();
			grdCfrntMeth.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			dsOutCfrntMeth_OnRowPosChanged();
			break;
		case "divBtnRowRacerArrangeStnd" :
			if ( !fnc_DeleteCheck(dsOutRacerArrangeStnd, "chk", "1") ) return;
			grdRacerArrangeStnd.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			break;
		case "divBtnRowCfrntDay" :
			if ( dsOutCfrntDay.RowCount() > 0 ) {
			    dsOutCfrntDay.row = dsOutCfrntDay.GetRowCount() - 1;
			    var nDayOrd = dsOutCfrntDay.GetColumn(dsOutCfrntDay.currow, "DAY_ORD");
				if ( fnc_Message(SNIS_COM_1004, "Y", nDayOrd) ) {
					dsOutCfrntDay.DeleteRow(dsOutCfrntDay.GetRowCount() - 1);

					fnc_DeleteRow(dsOutCfrntRace, "DAY_ORD", nDayOrd);
                    dsOutCfrntRace.Sort("DAY_ORD,RACE_NO", true);
				}
			} else {
				fnc_Message(SNIS_COM_1003, "N");                      //삭제 자료 없음 정보 Display
			}

			grdCfrntDay.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			break;
	}
}

