/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( sServiceName == "e02010020-service:searchRacerArrangeStnd" ) {
        ServiceMessage = NULL;
    }

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		fce_AddCodeRow(dsOutRacerGrd, "전체", "00");
		fcSearch();
        return;
	}

	// 대진방식 조회
	if ( sServiceName == "e02010020-service:searchCfrntMeth" ) {
        dsOutCfrntMeth.AddColumn("chk");
        fnc_SetStatus(SNIS_BOA_0003, dsOutCfrntMeth.GetRowCount());
        return;
	}

	// 주선기준조회
	if ( sServiceName == "e02010020-service:searchRacerArrangeStnd" ) {
        dsOutRacerArrangeStnd.AddColumn("chk");
        return;
	}

	// 저장 후
	if ( sServiceName == "e02010020-service:saveCfrntMeth" ) {
        dsOutCfrntMeth.AddColumn("chk");
        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

